#js-app .VideoMetaContainer .GenreRankMeta .GenreRankMeta-genreName {
	color: var(--cajBodyTextColorPale);
}
#js-app .VideoMetaContainer .GenreRankMeta .GenreRankMeta-highestPast {
	color: var(--cajDescriptionClosedTextColor);
}
/* ***** my-memory button */
#js-app .VideoMetaOverflowMenuContainer {
	top: 12px;
}
#js-app .VideoMetaOverflowMenuContainer .VideoMetaOverflowMenuOpenButton,
#js-app .VideoMetaOverflowMenuContainer-menu .CloseButton {
	border-radius: 40px;
	width: 40px;
	height: 40px;
}
#js-app .VideoMetaOverflowMenuContainer .VideoMetaOverflowMenuOpenButton {
	margin-left: 10px;
	background-color: var(--cajBodyButtonBackgroundColorPale);
	position: relative;
}
#js-app .VideoMetaOverflowMenuContainer-menu {
	top: -1px;
	right: -1px;
	text-align: left;
}
#js-app .VideoMetaOverflowMenuContainer-menu .CloseButton {
	top: 0;
	right: 0;
	padding: 12px;
	background: var(--cajBodyButtonBackgroundColorPale);
}
#js-app .VideoMetaOverflowMenuContainer .VideoMetaOverflowMenuOpenButton svg,
#js-app .VideoMetaOverflowMenuContainer-menu .CloseButton svg {
	fill: var(--cajBodyButtonMarkColorPale);
}
/* ***** my-memory popup menu */
#js-app .VideoMetaOverflowMenuContainer-menu {
	background-color: var(--cajDefaultBackgroundColor);
	color: var(--cajDefaultTextColor);
	border: 1px solid var(--cajDefaultBorderColor);
}
#js-app .VideoMetaOverflowMenuContainer-menu a {
	color: var(--cajDefaultLinkColor) !important;
}
#js-app .VideoMetaOverflowMenuContainer-menu .VideoMetaOverflowMenuContainer-menuBlock:not(:first-of-type) {
	border-top: 1px solid var(--cajDefaultBorderColor);
}
#js-app .VideoMetaOverflowMenuContainer-menuHeading {
	color: var(--cajDefaultTextColorPale);
}



/* *** description */

#js-app .VideoDescription.is-expanded {
	padding-bottom: 4px;
	border-bottom: 0px none;
	overflow: auto;
	overflow-x: auto;
	overflow-y: auto;
}
#js-app .VideoDescriptionExpander .VideoDescriptionExpander-switch.VideoDescriptionExpander-switchCollapse,
#js-app .VideoDescriptionExpander .VideoDescriptionExpander-switch.VideoDescriptionExpander-switchExpand {
	top: -12px;
	right: 0px;
	bottom: auto;
	overflow: hidden;
	width: 100%;
	height: 12px;
	background: none;
	background-color: transparent;
	border-bottom: 1px var(--cajDescriptionOpenerBorderStyle) var(--cajDescriptionOpenerBorderColor);
	font-size: 0px;
	text-align: center;
	padding-left: 0px;
	padding-right: 0px;
}
#js-app .VideoDescriptionExpander .VideoDescriptionExpander-switchCollapse::before {
	border-top: 0px none;
	border-right: 14px solid transparent;
	border-bottom: 12px solid var(--cajDescriptionOpenerMarkerColor);
	border-left: 14px solid transparent;
	vertical-align: top;
 }
#js-app .VideoDescriptionExpander .VideoDescriptionExpander-switchExpand::before {
	border-top: 12px solid var(--cajDescriptionOpenerMarkerColor);
	border-right: 14px solid transparent;
	border-bottom: 0px none;
	border-left: 14px solid transparent;
	vertical-align: top;
}
#js-app .VideoDescription-html,
#js-app .VideoDescription-plain {
	padding-left: 11px;
	padding-right: 11px;
	padding-top: 3px;
}
#js-app .VideoDescription .VideoDescription-html {
	color: var(--cajBodyTextColor);
}
#js-app .VideoDescription-plain {
	color: var(--cajDescriptionClosedTextColor);
}

