style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vidlii.com"){
/* VidLii theme based on the GTK Numix theme*/
  body{background:#dedede;font-family: "Roboto","Segoe UI"}
  #hd_vidlii{padding:5px;}
  #pr_sel li,.big_sel{
    color: /*[[accent-color]]*/ !important;
    background: #f6f6f6 !important;
    border: 1px #9a9a9a solid;
    border-bottom:3px /*[[accent-color]]*/ solid !important;
}
  #pr_hd ul a, .n_head ul a{margin:0;}
  #pr_hd li, .n_head li,.w_big_btn{
    color: #707070;
    border-radius:0;
    border:1px solid #9a9a9a;
    border-bottom:3px #e6e6e6 solid;
    background: #e6e6e6;
    transition: all linear 0.2s;
}
  #pr_hd li:hover, .n_head li:hover, .w_big_btn:hover {
    color: #707070;
    background: #f0f0f0 !important;
    border:1px solid #9a9a9a;
    border-bottom:3px #e6e6e6 solid;
}
  .n_head .pr_hd_bar {
    border:1px solid #c1c1c1;
    background:#fff;
}
  .search_button, .s_search input[type="submit"] {
    border-radius: 2px;
    cursor: pointer;
    border: 1px solid #b2b2b2;
    background: #f0f0f0;
    color:#555;
    
}
    .search_button:hover, .s_search input[type="submit"]:hover{
    border: 1px solid #a0a0a0;
    background: #ffffff;
}
   .search_button:active, .s_search input[type="submit"]:active{
     background: #707070;
     color:#ffffff;
     border:1px solid #9b9b9b;
   }
    .search_button,.yel_btn, .s_search input[type="submit"]{transition: all ease-out 0.2s;}
.yel_btn {
    background: #ffe35f;
    border: 1px solid #F4C800;
    border-radius: 2px;
}
  .yel_btn:hover{background: #fff5c5;}
  .yel_btn:active{background: #a99225;color:#ffffff !important;}
  #name_nav a:first-of-type,#name_nav a:last-of-type,#name_nav > div {
    border-radius: 0;
}
  #name_nav, #s_toggle a, #s_toggle2 a{background:#444;}
  #s_toggle a, #s_toggle2 a{color:#eeeeee; transition: all linear 0.07s;}
  #name_nav a{border:1px solid #404040;color:#eeeeee;transition: all linear 0.07s;}
  #name_nav a:hover, #s_toggle a:hover, #s_toggle2 a:hover {
    background: /*[[accent-color]]*/;
    color:#ffffff;
    text-decoration: none;
}
  #mod_selector,.wdg > div:last-of-type, .wt_des{border:1px solid #bdbdbd;}
  #mod_selector,.last_5,.whats_new,.mv_wr,.mv_under,#inbox_wdg,.wdg,.you_wnt,footer,.vc_cats,.vc_r,.w_btm,.w_actions,#playerWindow,#pr_hd li, .n_head li,.n_head .pr_hd_bar,.wt_des{box-shadow: 0 2px 6px rgba(0,0,0,0.1);}
  
  .w_videos{box-shadow:inset 0 0 2px rgba(0,0,44,0.7);background: #eee;}
#mod_selector.hddn {
    display:inherit !important;
  height: 0 !important;
  overflow: hidden;
  opacity: 0;
}
  #mod_selector{transition: all 0.4s;height:210px;}
  .vc_r{background: white;border-radius: 5px;}
  #vc_videos > div,.vc_channels > div {
    margin-left:20px;
    margin-right:20px;
    margin-bottom: 17px;
    width: 140px;
}
  .vc_hd {border-radius:0}
  #vc_selec{background: white;box-shadow:none;border-radius: 0;}
  .vlControls{border-radius:0!important;}
  .w_btm,.big_sel,.w_actions,#mod_selector,.wdg,.last_5{background:#f2f2f2!important}
  .wdg{border-radius: 10px;}
  .big_sel a{color:/*[[accent-color]]*/ !important}
  .w_big_btn > a,.w_big_btn:hover > a, .vc_hd li a{color: #707070;text-decoration: none;}
  a{color:/*[[accent-color]]*/;}
  a:hover{text-decoration:none;color:#555555;}
}
