style.css:
@-moz-document domain("neko-san.fr") {
.last-episodes-nav  .navigation{
    /* inverse the nbavigation buttons order*/
    display: -webkit-flex; /* Safari */
    -webkit-flex-direction: row-reverse; /* Safari 6.1+ */
    display: flex;
    flex-direction: row-reverse;
}
.navigation i.fa.fa-chevron-left:before{
    /*change the navigation button "Previous" (>) for the "<" char*/
    content: "\f054";
}

.navigation i.fa.fa-chevron-right:before{
    /*change the navigation button "Next" (<) for the ">" char*/
    content : "\f053";
}

}
