style.css:
@-moz-document domain(tweetdeck.twitter.com) {
html.dark .stream-item, html.dark div.js-column-scroller { background-color:#1b2836 !important; }
html.dark .tweet-body a, html.dark .tweet-body a:hover, html.dark .tweet-body a:focus, html.dark .tweet-body a:active { color:#1da1f2 !important; }
html.dark .app-columns-container{ background-color:#141d26 !important; }
html.dark .column-header{ background-color:#141d26 !important; }
html.dark .app-header, html.dark .column-nav-item, html.dark .app-navigator, html.dark .app-title{ background-color:#141d26 !important; }
}
