style.css:
@-moz-document url-prefix("https://japaneseclass.jp/readings/read/") {
div.question > span.kanji-xxl.inline-block rt {
    display: none;
    font-size: 30px;
    line-height: 30px;
    color: silver;
}

div.question:hover > span.kanji-xxl.inline-block rt {
    display: inline;
}

div.question > span.kanji-xxl.inline-block rt::before {
    content: "(";
}

div.question > span.kanji-xxl.inline-block rt::after {
    content: ")";
}
}
