style.css:
@-moz-document domain(openvk.gfx3336007.com) {
#button
{
    border-top-color: #f00;
    border-bottom-color: #f00;
    border-left-color: #f00;
    border-right-color: #f00;
    background: url('http://progi.my1.ru/openvkstyle/vsouzebutton.png');
}
#head-wrapper {
    background: url('http://progi.my1.ru/openvkstyle/vsouze.png');
    background-color: #c70000;
}
#head-button {
    background: url('http://progi.my1.ru/openvkstyle/vsouzecolor.png');
    background-color: #e8e8e8;
    border-radius: 0%;
}
#Tabs {
	border-bottom: 1px solid #d60000;
    border-bottom-width: 1px;
    border-bottom-style: solid;
    border-bottom-color: rgb(214, 0, 0);
}
#Tabs .SelectedTab {
    background-color: #d00000;
}
#Tabs .Tab a {
    color: #f00;
}
#left-menu a {
    color: #b70000;
}
h4 {
    color: #d40000;
}
a {
    color: #e00000;
}
#content-wall-title {
    background: #ffd3d3;
    border-top: #cc0000 solid 1px;
    color: #bb3f3f;
}
#content-wall-send {
    background-color: #ffeaea;
}
}
