style.css:
@-moz-document domain(the-division-map.com) {
	.bonus {height:0 !important;
    opacity:0;
		/*Animation*/
  -webkit-transition: height 1s ease;
     -moz-transition: height 1s ease;
       -o-transition: height 1s ease;
      -ms-transition: height 1s ease;
          transition: height 1s ease;
}

	.set-piece {display: none !important;}

	.classified {display: flex !important;}

	.set-piece .name .decor {width:100%;}

	.set-piece .name span {padding:0 10px 0 10px;}

	.set-piece:hover > .bonus {height: 300px !important;opacity: 1 !important;}
}
