style.css:
@-webkit-document regexp('.*dashboard\\.heroku\\.com/apps/.*/logs.*') {
  nav.glostick {
    display: none;
  }

  body {
    padding: 0;
  }

  .build-view {
    padding: 10px;
  }

  .app-nav {
    display: none;
  }

