style.css:
@-moz-document domain("duckduckgo.com") {


.svg .logo_homepage
{
		background-image: url("https://i.imgur.com/s29uVFj.png");
		width: 100%;
		background-size: 100% auto;
		background-position: bottom center;
}

.logo-wrap--home
{
		max-width: 634px;
		width: 70%;
}



.svg .header__logo {
    background-image: url("https://i.imgur.com/NDQCUrS.png") !important;
    background-size: 64px 64px !important;
    width: 120px!important;
    height: 60px!important;
    margin-top: -10px!important;
    margin-left: -5px!important;
}
.zcm__link {
    height: 15px;
    line-height: 15px;
    padding: 8px 8px 12px;
    -webkit-font-smoothing: unset!important;
    font-weight: 400!important;
    color: #777!important;
}
.zcm-wrap--header {
    margin-left: -5px!important;
}
.dropdown__button {
    color: #777!important;
}
.header--aside__msg,
.header--aside__twitter {
    display: none!important;
}
.feedback-btn {
    display: none!important;
}
.header__button--menu {
    margin-top: -25px!important;
}
/*.result__check {
    display:block!important;
}*/

body {
    background: white !important;
    font-family: arial, sans-serif;
    color: #222;
}
.header__logo-wrap {
    margin-left: 20px;
    width: 120px;
}
.module {
    margin-left: 0px;
}
.module__body {
    border-bottom: solid 1px #ebebeb;
    padding-bottom: 25px;
}
.module--about__img {
    padding-left: 0px;
    padding-bottom: 0px;
}
.module__title {
    font-size: 19px;
    color: rgba(0, 0, 0, .87);
    line-height: 1.2;
    font-family: arial, sans-serif-light, sans-serif;
    font-weight: normal;
    text-align: left;
}
.module__text {
    line-height: 1.24;
    font-size: 13px;
    text-align: left;
    color: #222;
}
.module__more-at js-about-module-more-at-inline {
    color: red;
}
.about-info-box {
    padding-top: 20px;
}
.about-info-box__info-row {
    border-top: none;
}
.about-info-box__info-label {
    font-size: 13px;
    text-align: left;
    color: #222;
}
.about-info-box__info-value {
    line-height: 1.24;
    font-size: 13px;
    text-align: left;
    color: #222;
}
.header__search {
    padding-left: 56px;
    min-width: 634px;
}
#search_form_homepage,
#search_form {
    /*box-shadow: 0px 1.5px 2px .5px lightgray !important;*/
    
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.16), 0 0 0 1px rgba(0, 0, 0, 0.08)!important;
    background-color: white !important;
    border-radius: 1.5px !important;
    transition: all 200ms !important;
    font-size: 100% !important;
    border: 0px!important;
}
#search_form_homepage:hover,
#search_form:hover,
#search_form_homepage:focus,
#search_form:focus,
.search__autocomplete {
    box-shadow: 0 3px 8px 0 rgba(0, 0, 0, 0.2), 0 0 0 1px rgba(0, 0, 0, 0.08)!important;
    /*border: 1px solid rgba(0,0,0,0.15);*/
}
#search_button_homepage,
#search_form_input_clear {
    opacity: 0 !important;
    transition: all 500ms !important;
}
#search_button_homepage:hover,
#search_form_input_clear:hover,
#search_button {
    opacity: 1 !important;
    background-color: white !important;
    color: #4285F4 !important;
    border-top: 1px solid rgba(0, 0, 0, 0.15);
    border-bottom: 1px solid rgba(0, 0, 0, 0.15);
    border-right: 1px solid rgba(0, 0, 0, 0.15);
}
.zcm {
    padding-top: 17px;
    color: #666;
    font-size: 13px;
    outline: none;
    font-family: arial, sans-serif;
}
.zcm-wrap {
    padding-left: 150px;
}
.zcm__link.is-active {
    color: #4285F4 !important;
    border-bottom-color: #4285F4 !important;
    font-weight: bold !important;
    border-bottom: solid 3px #de5833;
}
.zcm__link.is-highlighted,
.zcm__link:focus {
    color: #4285F4 !important;
}
.header-wrap {
    border-top-width: 0px !important;
    background-color: #FAFAFA !important;
    box-shadow: 0px 1px 0px #EBEBEB !important;
}
#header_wrapper {
    min-height: 105px;
}
#header {
    min-height: 105px;
}
#duckbar {
    min-height: 58px;
}
#links_wrapper {
    padding-left: 150px;
}
.result__title {
    font-size: 18px;
    margin: 0;
    padding: 0;
    font-family: arial, sans-serif;
    line-height: 1.2;
    text-align: left;
    color: #1a0dab;
    padding-bottom: 2px;
}
.result__snippet {
    margin: 0;
    padding: 0;
    font-size: small;
    color: #494949;
}
.result__icon {
    display: none;
}
.result__url {
    font-size: 14px;
    color: #006621;
    font-style: normal;
    line-height: 16px;
    font-weight: normal;
}
a.result__a {
    font-weight: normal;
    color: #1a0dab;
}
a.result__a:visited {
    font-weight: normal;
    color: #660099;
}
b {
    font-weight: normal;
}
.msg__line {
    color: #dd4b39;
    font-size: 18px;
}
.js-spelling-suggestion-link {
    color: #1a0dab;
    font-size: 18px;
    cursor: pointer;
    font-weight: bolder;
    font-style: italic;
}
.msg__line--small {
    font-size: 0.9em;
    color: #333;
}
.result {
    margin-bottom: 0.5em;
}
.result.highlight {
    background-color: white !important;
}
.header--aside {
    padding-top: 20px;
}
.header--aside__msg {
    padding-top: 20px;
}
.module--news {
    margin-left: 0px !important;
}
.cw {
    max-width: 100%;
}
