/* ==UserStyle==
@name Jira Carbon
@namespace Reptarsrage
@version 1.0.0
@homepageURL https://github.com/Reptarsrage/jira-carbon
@updateURL https://reptarsrage.github.io/jira-carbon/jira-carbon.css
@license CC-BY-SA-4.0
@author Reptarsrage
@advanced color Primary "Primary Background" #21252D
@advanced color Secondary "Secondary Background" #2C3037
@advanced color Tertiary "Tertiary Background" #434953
@advanced color Border "Border Color" #3C414C
@advanced color Link "Link Color" #4B97F8
@advanced color Highlight "Highlight Color" #6A25AA
@advanced color Primary Text "Primary Text Color" #EBEBEB
@advanced color Secondary Text "Secondary Text Color" #BEBEBE
@advanced color Tertiary Text "Tertiary Text Color" #888888
==/UserStyle== */

@-moz-document regexp("https?://.*\\.atlassian\\.net/.*"), regexp("https?://jira\\..*")
{
  html,
  body {
    background-color: /*[[Primary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
  }

  html,
  body,
  body * {
    border-color: /*[[Border]]*/ !important;
  }

  a {
    color: /*[[Link]]*/ !important;
  }

  input {
    background: /*[[Secondary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
  }

  em {
    color: /*[[Secondary Text]]*/ !important;
  }

  /* Track */
  ::-webkit-scrollbar-track {
    background: /*[[Secondary Text]]*/ !important;
  }

  /* Handle */
  ::-webkit-scrollbar-thumb {
    background: /*[[Border]]*/ !important;
  }

  /* Handle on hover */
  ::-webkit-scrollbar-thumb:hover {
    background: /*[[Border]]*/ !important;
  }

  ::selection {
    background: /*[[Highlight]]*/ !important;
  }

  ::-moz-selection {
    background: /*[[Highlight]]*/ !important;
  }

  ::placeholder {
    /* Chrome, Firefox, Opera, Safari 10.1+ */
    color: /*[[Secondary Text]]*/ !important;
    opacity: 1;
    /* Firefox */
  }

  :-ms-input-placeholder {
    /* Internet Explorer 10-11 */
    color: /*[[Secondary Text]]*/ !important;
  }

  ::-ms-input-placeholder {
    /* Microsoft Edge */
    color: /*[[Secondary Text]]*/ !important;
  }

  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group:focus,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group:focus,
  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group:hover,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group:hover,
  .aui-sidebar[aria-expanded="false"] .aui-sidebar-group.active,
  .aui-sidebar-submenu[aria-expanded="false"] .aui-sidebar-group.active {
    background: transparent !important;
  }

  .action-description {
    color: /*[[Secondary Text]]*/ !important;
  }

  .issue-drop-zone__button {
    color: /*[[Link]]*/ !important;
  }

  form.aui div.description {
    color: /*[[Secondary Text]]*/ !important;
  }

  .jira-dialog .dialog-menu-group .dialog-menu-item.selected {
    color: /*[[Tertiary Text]]*/ !important;
  }

  .jira-dialog .dialog-menu-group .dialog-menu-item {
    color: /*[[Link]]*/ !important;
  }

  .aui-select2-container.select2-container .select2-choice,
  .aui-select2-container.select2-container a.select2-choice,
  .aui-select2-container.select2-container .select2-choice:visited {
    background: /*[[Primary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
    text-shadow: none !important;
    border-color: /*[[Border]]*/ !important;
  }

  .ct-toolbar {
    background: /*[[Primary]]*/ !important;
  }

  .jira-multi-select > .representation .value-item {
    -moz-border-radius: 0 !important;
    border-radius: 0 !important;
    background: /*[[Primary]]*/ !important;
    color: /*[[Link]]*/ !important;
  }

  .editable-field .overlay-icon {
    background: /*[[Secondary]]*/ !important;
  }

  .ghx-column-headers .ghx-release.ghx-disabled {
    color: /*[[Tertiary Text]]*/ !important;
  }

  .ghx-controls-filters dd a {
    color: /*[[Primary Text]]*/ !important;
  }

  .ghx-controls-filters dd a.ghx-active {
    color: /*[[Primary Text]]*/ !important;
    background: /*[[Secondary]]*/ !important;
  }

  #dashboard,
  .gadget .dashboard-item-content {
    background-color: /*[[Primary]]*/ !important;
  }

  #footer-logo {
    background-color: /*[[Primary]]*/ !important;
  }

  #issue-content {
    background-color: /*[[Primary]]*/ !important;
  }

  #issuetable,
  .issue-table,
  #issuetable > thead .active,
  .issue-table > thead .active,
  #issuetable > tbody > tr,
  .issue-table > tbody > tr,
  #issuetable > tfoot > tr,
  .issue-table > tfoot > tr {
    background-color: /*[[Primary]]*/ !important;
  }

  .activity-item-description .user-content {
    color: /*[[Secondary Text]]*/ !important;
  }

  .activity-item-description blockquote {
    background-color: /*[[Secondary]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
  }

  .activity-item-summary {
    color: /*[[Primary Text]]*/ !important;
  }

  .activity-item-info {
    color: /*[[Secondary Text]]*/ !important;
  }

  #activity-stream-show-more:hover {
    background-color: /*[[Tertiary]]*/ !important;
  }

