    background: url("data:image/svg+xml;base64,") !important;
  }

  .agile-icon-report {
    background: url("data:image/svg+xml;base64,") !important;
  }

  .agile-icon-work {
    background: url("data:image/svg+xml;base64,") !important;
  }

  .ajs-layer {
    background-color: /*[[Secondary]]*/ !important;
  }

  .applinks-auth-request {
    background-color: /*[[Secondary]]*/ !important;
  }

  .aui-badge {
    background-color: /*[[Link]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
  }

  .aui-button.aui-button-primary {
    background-color: /*[[Link]]*/ !important;
    color: /*[[Primary Text]]*/ !important;
  }

  .aui-button.ghx-dropdown-trigger::after,
  .aui-buttons .aui-button.aui-button-primary.ghx-dropdown-trigger::after {
    border-top-color: /*[[Primary Text]]*/ !important;
  }

  .aui-dropdown2.aui-style-default {
    background: /*[[Primary]]*/ !important;
  }

