    background-color: transparent !important;
    border-color: #c6ae65 !important;
    color: #c6ae65 !important;
  }

  .aui-message.error,
  .aui-message-error {
    background: /*[[Secondary]]*/ !important;
    background-color: /*[[Secondary]]*/ !important;
    color: /*[[Secondary Text]]*/ !important;
  }

  .aui-message.hint,
  .aui-message-hint,
  .aui-message.info,
  .aui-message-info {
    background-color: /*[[Secondary]]*/ !important;
    color: /*[[Secondary Text]]*/ !important;
  }

