    .aui-sidebar-group.aui-sidebar-group-actions::after,
  .aui-sidebar-submenu[aria-expanded="false"]
    .aui-sidebar-group.aui-sidebar-group-actions::after {
    background-color: /*[[Primary]]*/ !important;
