style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("portal.upce.cz") {

#banner { /* zahlavi */
 display: none;
}
.tabs {
 background: #E61000 !important;
}
.tabs .LTabLeftLow, .tabs .LTabRightLow, .tabs .LTabLeft, .tabs .LTabRight { /* menu */
 display: none;
}
.tabs .LTabLow, .tabs .LTab { /* menu */
 height: 1em !important;
 border: none !important;
 border-right: 2px solid white !important;
 padding: 0.2em 0.5em !important;
}
#portal-page-actions { /* odhlaseni */
 display: none;
}
#breadcrumbs .FolderList { /* odhlaseni */
 display: none;
}
#breadcrumbs {  /* odhlaseni */
 display: block;
 position: absolute;
 visibility: hidden;
 right: 0;
 top: -1px;
 width: 10px;
 height: 1px !important;
 border: none;
 padding: 0;
 margin: 0;
}
#breadcrumbs span a {
 display: block !important;
 visibility: visible;
 background: #E61000;
 color: white;
 position: absolute !important;
 right: -53px !important;
 top: -3px !important;
 line-height: 1.7em;
 padding: 0 0.3em;

}
#breadcrumbs span a:hover {
 text-decoration: none !important;
}

}
