	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}


	/*Outline for links*/
 	a
 	{
		outline-color: var(--main-color) !important;
	}


	/**Removing text decorations*/
	*
	{
		text-decoration: none !important;
	}


	/*Links*/
	a, a:visited
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	a:hover, a:hover span
	{
		color: var(--main-color) !important;
		opacity: 1;
	}

	/*Body*/
	body
	{
		background: var(--main-background) !important;
		color: var(--main-text) !important;
	}


	.user-pic img
	{
		border-radius: 10% !important;
	}

	/*Navbar*/
	#header
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.notice
	{
		background: var(--hover-background) !important;
	}
	#logo a
	{
		filter: grayscale(100%) brightness(250%);
	}
	#logo a:hover
	{
		filter: grayscale(100%) brightness(500%);
	}


	/*Search*/
	textarea, input, select
	{
		background: var(--main-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
		color: var(--main-text) !important;
		text-shadow: none !important;
	}
	.btn
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
		color: var(--main-text) !important;
		text-shadow: none !important;
	}
	.btn:hover
	{
		background: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
		color: var(--main-color) !important;
	}
	textarea:hover, input:hover, textarea:active, input:active, textarea:focus, input:focus
	{
		border-color: var(--main-color) !important;
	}
	#header .btn .icon-search
	{
		filter: grayscale(100%) brightness(250%);
	}
	#header .btn:hover .icon-search
	{
		filter: grayscale(100%) brightness(500%);
	}

	#start, #start::before, #start::after
	{
		background: var(--second-background) !important;
		border: none !important;
		box-shadow: var(--shadow) !important;
	}

	/*Sidebar*/
	#menu::before, #menu::after, #menu
	{
		background: transparent !important;
		border: none !important;
		box-shadow: none !important;
	}
	#menu ul li.active a
	{
		background: var(--second-background) !important;
		border: none !important;
		border-right: 3px solid var(--main-color) !important;
	}
	#menu ul li:not(.active) a:focus, #menu ul li:not(.active) a:hover, #menu ul li.active a:hover
	{
		border-right: 3px solid var(--hover-background) !important;
		background: var(--hover-background) !important;
	}
	#menu hr,#menu hr::after
	{
		background: var(--hover-background) !important;
	}


	/*Icons*/
	[class^="icon-"], [class*=" icon-"]
	{
		filter: invert(100%) grayscale(100%) brightness(500%);
	}


	/*Headers*/
	h2::after, h3::after
	{
		border: none !important;
		background: var(--hover-background) !important;
	}
	.updated span, .queue-pos span
	{
		opacity: .5 !important;
	}


	/*Scores*/
	.bar
	{
		background: var(--dimer-text) !important;
	}
	.bar .filled
	{
		background: var(--main-color) !important;
	}


	/*List*/
	table.tablesorter thead tr .header, table.tablesorter thead th
	{
		background-color: var(--hover-background) !important;
		border: none !important;
	}
	table.tablesorter thead tr .headerSortDown, table.tablesorter thead tr .headerSortUp
	{
		background: var(--second-background) !important;
		border: none !important;
	}

	table.tablesorter tbody tr:hover td
	{
		background-color: var(--hover-background) !important;
	}
	.icon-status-finished, .icon-status-dropped, .icon-status-completing, .icon-status-planned
	{
		filter: grayscale(100%) brightness(250%);
	}
	.higher
	{
		color: var(--green) !important;
	}
	.lower
	{
		color: var(--red) !important;
	}

	/*Ratings*/
	/*Grid*/
	path[stroke="#f5f5f5"],path[stroke="#ddd"]
	{
		stroke: var(--hover-background) !important;
	}
	/*Bars*/
	rect[stroke="#FFFFFF"]
	{
		fill: var(--main-text) !important;
		stroke: var(--hover-background) !important;
	}
	rect[stroke="#FFFFFF"]:hover
	{
		fill: var(--main-color) !important;
		stroke: var(--hover-background) !important;
	}
	/*Axis*/
	text, .highcharts-axis-labels.highcharts-xaxis-labels text, .highcharts-axis-labels.highcharts-yaxis-labels text, .highcharts-yaxis-title
	{
		fill: var(--main-text) !important;
	}


	/*History*/
	.history-monthly table .box
	{
		background: var(--main-color) !important;
	}


	/*Recomendations*/
	.original .details-wrapper .header::after
	{
		background: var(--hover-background) !important;
	}
	.missing table thead tr th
	{
		background: var(--second-background) !important;
		border: none !important;
	}
	.missing table tbody tr:nth-of-type(2n) td
	{
		background: var(--second-background) !important;
		box-shadow: none !important;
	}
	.missing table tbody tr:nth-of-type(2n+1) td
	{
		background: var(--hover-background) !important;
		box-shadow: none !important;
	}
	.icon-small-close
	{
		filter: grayscale(100%) brightness(250%);
	}
	.icon-small-close:hover
	{
		filter: grayscale(100%) brightness(500%);
	}


	/*Achievements*/
	.progress-wrapper
	{
		opacity: 1 !important;
	}
	.progress
	{
		background: var(--dimer-text) !important;
	}
	.progress .bar
	{
		background: var(--main-color) !important;
	}
}
