style.css:
@-moz-document domain("ecosia.org") {
.body-index{
background: url(https://source.unsplash.com/user/anywombat/likes/1920x1080);
width: 100%;
}
.search-section{
background: transparent;
}
body {
	color: white;
}
.total-counter-number {
	display: inline-block;
	height: 50px;
	overflow: hidden;
	letter-spacing: 3px;
	background: white;
	border-radius: 2px;
}
.total-counter-banner {
	font-size: 30px;
    font-weight: 0;
    margin-left: 220px;
}
.total-counter {
	margin-top: -55px;
}
.sidebar {
    background: white;
    color: black;
}

}

@-moz-document url-prefix("https://www.ecosia.org/search?") {
.result-wrapper {
background-image: url(https://source.unsplash.com/user/anywombat/likes/1920x1080)!important;
background-repeat: repeat;
background-attachment: fixed;
}
body {
	color: black;
}
.card-desktop {
background: white;
border-radius: 2px;
padding-left: 10px;
padding-right: 10px;
}
.entity {
box-shadow: none;
}
.entity-subtitle {
color: black;
}
.navbar-row {
background: #ededed
}
.search-form-wrapper {
	border-radius: 25px;
    border: none;
}
.search-form-input {
	border-radius: 25px;
    z-index: 20;
}
.search-form-button {
	border-radius: 25px;
    z-index: 20;
}
.search-header{
    padding-bottom: 0px;
}
.typeahead{
	border: none;
    border-radius: 25px;
    margin-top: -40px;
    padding-top: 40px;
}

}
