style.css:
@-moz-document domain("qanonposts.com"), domain("qanon.pub") {
body {
    background: #222222;
    color: #cccccc;
}

header.row, button {
    background: #333333;
    color: #eeeeee;
}

a {
    color: #4444cc;                                        
}

time {
    background: inherit !important;                                        
    color: #eeeeee !important;
}

article {
    background: #333333 !important;
}

article blockquote {
    background: #444444 !important;
}
}
