style.css:
@-moz-document domain(profdinfo.com) {
  :root{
    --bg-color-gray1: #252525;
    --bg-color-gray2: #525252;
    --bg-color-top: #484848;
    --bg-color-footer: #444444;
  }
  td[bgcolor="#66FF99"]{
    background-color: #989898;
  }
  td[bgcolor="#E0E0E0"]{
    background: none;
  }
  body {
    background-color: var(--bg-color-gray1);
    color: #f3f3f3;
  }
  a {
    color: /*[[color-link]]*/;
  }
  .Style3{
    color: /*[[color-class-link]]*/;
  }
  .Style4{
  	color: black;
  }
  #main{
    background-color: var(--bg-color-gray2);
    border-radius: 4px;
    padding-top: 1em;
  }
  #subheader{
    background: #afadad;
    border-radius: 5px;
  }
  #notes pre{
    background-color: #232323;
    color: /*[[color]]*/ !important;
    border-radius: 4px;
    margin-top: 1em;
    margin-bottom: 1em;
  }
  .commande{
    background-color: #2b2b2b;
    border-radius: 4px;
    font-weight: bold;
  }
  #notes h2{
    color: #adadad;
    font-size: 19px;
  }
  #main #leftside{
    background: var(--bg-color-gray2);
    border-radius: 2px;
    padding-left: 5px;
    padding-right: 5px;
    margin-bottom: 1em;
  }
  #main #leftside .box {
    background: var(--bg-color-gray1);
    color: #fff;
    border-radius: 2px;
  }
  #main #rightside {
    color: #fff;
    padding-bottom: 5px;
  }
  h1{
    color: white;
  }
  #rightside{
    background: var(--bg-color-gray2);
    border-radius: 2px;
  }
  #main #rightside .box{
    background: #d8d8d8;
    color: #000;
    border-radius: 2px;
  }
  #footer{
    background: var(--bg-color-footer);
    border-radius: 5px;
    margin-bottom: 19px;
    padding: 3px;
    color: #ababab;
  }
  #main #leftside h2 {
    color: black;
    border-radius: 2px;
  }
  #main #rightside h2{
    color: black;
    border-radius: 2px;
  }
  #top{
    background: var(--bg-color-top);
    border-radius: 30px;
    height: 60px;
    margin: 10px;
  }
  #top .rights{
    background: none;
  }
  #top .lefts{
    background: none;
  }
  #topmenu{
    border-radius: 10px;
    margin: 10px 8px 0 8px;
  }
  #topmenu .lefts{
    background: none;
  }
  #topmenu .rights{
    background: none;
  }
  #notes{
    background: var(--bg-color-gray2);
    border-radius: 10px;
    margin-top: 20px;
    margin-bottom: 30px;
    padding-top: 1px;
    padding-left: 20px;
    padding-right: 20px;
  }
}
@-moz-document url(http://profdinfo.com/) {
  td{
    background: #525252;
    border-radius: 7px;
  }
  #notes{
    background: none;
    border-radius: 0;
    margin-top: 0;
    margin-bottom: 0;
    padding-top: 0;
    padding-left: 0;
    padding-right: 0;
  }
  #notes p {
    margin-top: 1em;
    background: #444444;
    padding: 8px;
    border-radius: 5px;
  }
  #notes h2{
    background: #444444;
    border-radius: 4px;
    padding: 4px;
  }
  #notes ul{
    background: #5d5d5d;
    border-radius: 4px;
    padding-top: 3px;
    padding-bottom: 5px;
    padding-right: 5px;
  }
  li{
    margin-top: 4px;
    margin-bottom: 3px;
  }
  img{
    border-radius: 5px;
  }
}
@-moz-document url(http://profdinfo.com/web/alexandreandrelesperance.html) {
  #main{
    height: 530px;
  }
  img[src*="alexandre"]{
    margin-top: 14px;
    border-radius: 5px;
  }
}
@-moz-document url(http://profdinfo.com/web/rachidkadouche.html) {
    #main{
    height: 450px;
  }
  img[src*="rachid"]{
    border-radius: 5px;
  }
}
@-moz-document url(http://profdinfo.com/web/maximebarakatt.html) {
  #main{
    height: 200px;
  }
}
@-moz-document url(http://profdinfo.com/web/etienneforest.html){
  img[src*="etienne"]{
    margin-top: 1em;
    border-radius: 5px;
  }
}
@-moz-document url(http://profdinfo.com/web/joanmorales.html){
  img[src*="joan2"]{
    border-radius: 5px;
  }
}
