style.css:
@-moz-document domain("/*[[domain]]*/") {
/*
NOTE: This section is things customizable in Cachet's Settings screen, under Theme. It's included here so it may be used as a user style.
If you want to install this as a theme for your Cachet instance, you should remove everything up to the comment saying "CUT HERE FOR CACHET ADMIN USE". Use these settings on the Theme page:
Background Color: #263238
Text Color: #ECEFF1
Red: #FF1744
Blue: #00BCD4
Green: #1DE9B6
Yellow: #FFC400
Orange: #FF9100
Metrics Fill: #0DCCC0
Links: #00FFCC
Background Fills: #455A64
*/
body.status-page {
  background-color: #263238;
  color: #eceff1;
}
p, strong { color: #eceff1 !important; }
.reds { color: #ff1744 !important; }
.blues { color: #00bcd4 !important; }
.greens { color: #1de9b6 !important; }
.yellows { color: #ffc400 !important; }
.oranges { color: #ff9100 !important; }
.metrics { color: #0dccc0 !important; }
.links { color: #00ffcc !important; }
.app-banner-padding {
  padding: 40px 0 !important;
}
.alert {
  background-color: #ffc400;
  border-color: #e5b000;
  color: white;
}
.alert.alert-success {
  background-color: #1de9b6;
  border-color: #1ad1a3;
  color: white;
}
.alert.alert-info {
  background-color: #00bcd4;
  border-color: #00a9be;
  color: white;
}
.alert.alert-danger {
  background-color: #ff1744;
  border-color: #e5143d;
  color: white;
}
.btn.links {
  color: #b28900;
}
.btn.btn-success {
  background-color: #1de9b6;
  border-color: #1ad1a3;
  color: white;
}
.btn.btn-success.links {
  color: #14a37f;
}
.btn.btn-success.btn-outline {
  background-color: transparent;
  border-color: #1de9b6;
  color: #1de9b6;
}
.btn.btn-success.btn-outline:hover {
  background-color: #1de9b6;
  border-color: #1ad1a3;
  color: white;
}
.btn.btn-info {
  background-color: #00bcd4;
  border-color: #00a9be;
  color: white;
}
.btn.btn-info.links {
  color: #008394;
}
.btn.btn-danger {
  background-color: #ff1744;
  border-color: #e5143d;
  color: white;
}
.btn.btn-danger.links {
  color: #b2102f;
}
.component {
  background-color: #455a64;
  border-color: #3e515a;
}
.sub-component {
  background-color: #455a64;
  border-color: #3e515a;
}
.incident {
  background-color: #455a64;
  border-color: #3e515a;
}
.status-icon {
  background-color: #455a64;
  border-color: #3e515a;
}
.panel.panel-message:before {
  border-left-color: #455a64 !important;
  border-right-color: #455a64 !important;
}
.panel.panel-message:after {
  border-left-color: #455a64 !important;
  border-right-color: #455a64 !important;
}
.footer a, .markdown-body {
  color: #eceff1;
}

/***** CUT HERE FOR CACHET ADMIN USE *****/

body.status-page .list-group .list-group-item.group-name {
  background-color: #607D8B;
}
body.status-page .list-group.components {
  border-color: #455A64;
}
body.status-page .list-group .list-group-item {
  border-color: #11171A;
}
body.status-page .list-group .list-group-item {
  background-color: #37474F;
}
body.status-page .list-group.components a {
  color: #ECEFF1 !important;
}
body.status-page .timeline .moment .status-icon {
  background: #11171A;
  font-size: 16px;
  text-align: center;
  line-height: 31px;
  border-color: #607D8B;
  border-width: 2px;
}
body.status-page .timeline .moment .status-icon.status-1 {
  background: #F44336;
}
body.status-page .timeline .moment .status-icon.status-2 {
  background: #FFC107;
}
body.status-page .timeline .moment .status-icon.status-3 {
  background: #03A9F4;
}
body.status-page .timeline .moment .status-icon.status-4 {
  background: #1DE9B6;
}
body.status-page .timeline .moment::before {
  background: #607D8B;
}
body.status-page .timeline .moment .status-icon .icon {
  position: initial;
  color: #000 !important;
}
body.status-page .timeline .moment .panel-message.list-group.components, body.status-page .timeline .moment .panel.panel-message, .incident {
  background-color: #37474F;
  border-color: #455A64;
}

.btn.btn-default {
  background: transparent;
  color: #B0BEC5;
  border-color: #607D8B;
}
.btn.btn-default:hover {
  background: #546E7A;
  color: #FFF;
  border-color: #607D8B;
}
.btn.btn-danger {
  background: transparent;
  color: #F44336;
  border-color: #607D8B;
}
.btn.btn-danger:hover {
  background: #F44336;
  color: #FFF;
  border-color: #607D8B;
}
body.status-page .timeline .moment .status-icon.status-1 .icon:before {
  content: "" !important;
}
body.status-page .timeline .moment .status-icon.status-3 .icon:before {
  content: "" !important;
}

.alert.alert-success {
  color: #000;
}
.group-toggle {
  display: none;
}

body.status-page .timeline .moment:last-child::before {
    height: 48px;
}

body.status-page .timeline .moment:first-child::before {
    top: 48px;
}
body.status-page .timeline .moment:only-child::before {
    display: none;
}

.panel.panel-message::before {
   border-left-color: #455A64 !important;
   border-right-color: #455A64 !important;
   left: 0px !important;
}
.panel.panel-message::after {
    border-left-color: #37474F !important;
    border-right-color: #37474F !important;
}
a {
    color: #00ffcc;
}
}
