style.css:
@-moz-document url-prefix("https://translate.google.com"), url-prefix("https://docs.google.com/spreadsheets/import/") {
body, body.displaying-saved-page, body.displaying-history-page {
    color: #ddd;
    background: #181818;
}
::selection {
    background: #999;
}
::-webkit-scrollbar {
    width: 10px;
}
::-webkit-scrollbar-track {
    background: #222;
}
::-webkit-scrollbar-thumb {
    background: #333;
}
::-webkit-scrollbar-thumb:hover {
    background: #2a2a2a;
}
/*HEADER*/
#gb {
    border-bottom: 1px solid #000;
}
.gb_zc:hover {
    background-color: #222;
}
header>div {
    background: #181818!important;
}
.gb_Wa svg, .gb_Fc svg {
    color: #fff;
}
.gb_He.gb_Ee {
    color: #ddd;
}
/*SIDE MENU*/
 .gb_Fc {
    background: #181818;
    color: #ddd;
}
.gb_Pc, .gb_Qc {
    color: #ddd;
}
.gb_Oc:not(:last-child) {
    border-bottom: 1px solid #000;
}
/*CONTENT*/
 .frame:before {
    background: #111;
}
/*text, docs*/
 .focus-wrapper {
    outline: none;
}
.homepage.translate-text .input-button.text-icon, .homepage.translate-docs .input-button.documents-icon, .header-button {
    border: 1px solid #1e1e1e;
    outline: none;
}
.homepage.translate-text .input-button.text-icon, .homepage.translate-docs .input-button.documents-icon {
    background: #1b1b1b;
}
.input-button.text-icon::before, .input-button.documents-icon::before {
    filter: saturate(0);
}
.header-button {
    color: #777;
}
/*languages*/
 .ls-wrap {
    background: #181818;
    border-top: 1px solid #1e1e1e;
    border-bottom: 1px solid #00000040;
}
.lang-btn {
    background-color: #181818;
}
.sl-sugg .goog-inline-block.jfk-button, .tl-sugg .goog-inline-block.jfk-button {
    color: #464646;
}
.language-selector-ripple {
    background: #ddd;
}
.ls-wrap .sl-sugg .goog-inline-block.jfk-button:focus, .ls-wrap .tl-sugg .goog-inline-block.jfk-button:focus, .ls-wrap .sl-more:focus, .ls-wrap .tl-more:focus, .ls-wrap .lang-btn:focus-within, .ls-wrap .swap:focus {
    background-color: #222;
}
.ls-wrap .sugg-fade {
    background: linear-gradient(left, rgba(255, 255, 255, 0), rgb(24, 24, 24));
    background: -moz-linear-gradient(left, rgba(255, 255, 255, 0), rgb(24, 24, 24));
    background: -webkit-linear-gradient(left, rgba(255, 255, 255, 0), rgb(24, 24, 24));
}
.swap-wrap {
    background: #181818;
}
.swap.jfk-button {
    background: #181818;
}
.ls-wrap .swap .jfk-button-img {
    filter: invert(1);
}
.swap:not(.jfk-button-disabled):active {
    background: #484848!important;
}
/*all languages*/
 .language-list-toolbar {
    background-color: #181818;
}
.language-list-toolbar .language-list-label {
    color: #ddd;
}
.language-list-search-box {
    background: #181818;
    border-bottom: 1px solid #00000040;
    box-shadow: none!important;
}
#sl_list-search-box, #tl_list-search-box {
    background: #181818;
    color: #ddd;
}
.language-picker-toolbar-image:focus, .language-list-search-button:focus .searchbutton-image, .language-list-back-button:focus {
    background-color: #222;
}
.language-list, .language_list_sl_list, .language_list_tl_list, .language_list_section {
    background-color: #181818;
}
.language_list_item_wrapper {
    background: #181818;
}
.language_list_item_wrapper.item-selected .language_list_item {
    color: #fff;
}
.language_list_item_wrapper.item-selected .language_list_item_icon, .language_list_item_wrapper.item-selected .detect_language_row_icon {
    filter: saturate(0) brightness(100);
}
.language_list_item_language_name, .detect_language {
    color: #ddd;
}
.language_list_item_wrapper.item-selected {
    background-color: #202020;
}
.language_list_item_wrapper:focus {
    border: 1px solid #222;
}
.language_list_item_wrapper:hover, .language_list_item_wrapper:focus {
    background-color: #222;
}
.language_list_item_wrapper.item-emphasized:not(.item-selected) .language_list_item_icon {
    filter: invert(1);
}
/*SOURCE*/
 .source-wrap, .source-input, .orig, .orig:disabled {
    background-color: #181818;
}
.orig {
    color: #ddd;
}
.clear .jfk-button-img {
    filter: invert(1);
}
.gt-spell-correct-message {
    background-color: #181818;
    color: #777;
}
.gt-spell-icon {
    filter: saturate(0);
}
.frame a:link {
    color: #fff;
}
.speech-button .jfk-button-img {
    filter: invert(1) saturate(0) contrast(100);
    opacity: 0.6;
}
.speech-data .speech-background {
    background: #000;
}
.src-tts .jfk-button-img {
    filter: invert(1);
}
.jfk-button-action {
    background: #333;
}
/*options*/
 .gt-is-mobile {
    background-color: #181818;
    border-color: #00000040;
    border-radius: 0;
}
.gt-is-mobile .gt-is-ld {
    border-top: 1px solid #00000040;
}
.gt-is-mobile .gt-is-sg {
    color: #ddd;
}
.gt-is-tr {
    color: #777;
}
.gt-is-mobile .gt-is-ca {
    filter: invert(1);
}
.gt-is-itm-hover {
    background: #222;
    border-color: #00000040;
}
/*drop-down menu*/
 .ita-kd-dropdown-menu {
    background: #222;
    border: 1px solid #00000040;
}
.ita-kd-menuitem {
    color: #aaa;
}
.ita-kd-menuitem-hover {
    background-color: #2a2a2a!important;
    color: #ddd!important;
}
.ita-kd-separator {
    border-top: 1px solid #00000040;
}
/*virtual keyboard*/
 .vk-box {
    background-color: #222;
    border: 1px solid #00000040;
}
.vk-btn {
    background: #181818;
    color: #ddd;
    border-color: #333;
}
.vk-btn.vk-sf-h {
    background: #333;
    color: #ddd;
    border-color: #444;
}
.vk-btn.vk-sf-a, .vk-btn.vk-sf-s {
    background: #000;
    color: #ddd;
    border-color: #444;
}
.vk-cap.vk-sf-b, .vk-t-btn {
    filter: invert(1);
}
/*handwrite*/
 .ita-hwt-ime-init-opaque {
    opacity: 1!important;
}
.ita-hwt-ime {
    background: #222;
    border: 1px solid #00000040;
}
.ita-hwt-ltr .ita-hwt-grip {
    background: none;
}
.ita-hwt-close {
    background-color: transparent;
}
.ita-kd-img {
    filter: invert(1);
}
.ita-hwt-candidates {
    border-top: 1px solid #00000040;
    background: #333;
}
.ita-hwt-candidate {
    background: #333;
    color: #ddd;
    border-color: #00000040;
}
.ita-hwt-candidate-hover {
    background: #222;
    color: #ddd;
}
.ita-hwt-candidate:active {
    background: #000;
    color: #ddd;
}
.ita-hwt-buttons {
    background-color: #333;
    border-top: 1px solid #00000040;
}
.ita-hwt-jfk-standard {
    background: #2b2b2b;
}
.ita-hwt-jfk-standard .ita-kd-img {
    opacity: 0.5;
}
.ita-hwt-jfk-standard.ita-hwt-jfk-hover {
    background: #222;
    border: 1px solid #00000040;
}
.ita-hwt-jfk-action.ita-hwt-jfk-hover {
    background: #222;
    border: 1px solid #00000040;
}
.ita-hwt-jfk-action.ita-hwt-jfk {
    background: #ddd;
    border: 1px solid #00000040;
}
/*definition*/
 .gt-lc.gt-lc-mobile .gt-cc-r-i {
    background: #181818!important;
}
.gt-lc.gt-lc-mobile .gt-cd {
    background-color: #1b1b1b;
    border: 1px solid transparent;
    border-top: 1px solid #1e1e1e;
}
.gt-card-ttl-txt {
    color: #ddd;
}
.gt-lc.gt-lc-mobile .gt-def-num {
    color: #777;
    border: 1px solid #777;
}
.gt-def-row {
    color: #ddd;
}
.gt-lc.gt-lc-mobile .gt-def-example, .gt-lc.gt-lc-mobile .gt-def-synonym-title {
    color: #777;
}
.gt-lc.gt-lc-mobile .gt-cd-cl, .gt-lc.gt-lc-mobile .gt-cd-ncl {
    background: none;
    color: #777;
    border: 1px solid #333;
}
.gt-lc.gt-lc-mobile .gt-ex-quote>svg {
    fill: #fff;
}
.gt-lc.gt-lc-mobile .gt-ex-text, .gt-lc.gt-lc-mobile .cd-expand-label {
    color: #ddd;
}
.gt-lc-mobile .gt-card-expanded:not(:last-child) .gt-syn-row {
    border-bottom: 1px solid #00000030;
}
.gt-lc.gt-lc-mobile .cd-expand-button {
    border-top: 1px solid #00000030;
}
/*RESULTS*/
 body:not(.with-lang-list) .results-container.empty {
    background-color: #1b1b1b;
    border-left: 1px solid #00000040;
	box-sizing: border-box;
}
.results-container {
    border-left: 1px solid #00000040;
    box-sizing: border-box;
}
    
.results-container, .result, .gt-edit, .contribute-target {
    background-color: #1b1b1b;
}
.result-dict-wrapper .translation {
    color: #ddd;
}
.trans-verified-button {
    filter: invert(1);
}
.result .unstarred>.jfk-button-img {
    filter: invert(1);
}
.result-transliteration-container .transliteration-content, .result-transliteration-container .truncate-link {
    color: #777;
}
.ttsbutton-res .jfk-button-img {
    filter: invert(1);
}
.copybutton .jfk-button-img, .morebutton .jfk-button-img {
    filter: invert(1);
}
.moremenu {
    background: #181818;
    border: 1px solid #00000040;
    color: #ddd;
}
.moremenu .goog-menuitem-highlight {
    background: #222;
}
.result-dict-wrapper .translation span{
    outline: none;
  }
.trans-edit, .trans-target-highlight {
  background: #222;
}
.alt-menu .goog-menuitem {
  color: #777;
}
  .alt-menu .goog-menuitem-highlight {
    background: #2a2a2a;
  }
.goog-menu.round-trip-content {
  color: #777;
}
  
/*contribute*/
 


:not(.empty) .placeholder, .result-dict-wrapper .translation, .contribute-target, #st-buttons .jfk-button {
    color: #ddd;
}
.gt-clear .jfk-button-img {
    filter: invert(1);
}
.st-stp1-epu-text {
    background: #1e1e1e;
}
/*share*/
 .share-panel {
    background: #1b1b1b;
    outline: none;
    border-top: 1px solid #1e1e1e;
}
.share-panel h3 {
    color: #ddd;
}
.share-panel li a span {
    color: #ddd;
}
/*translations*/
 .gt-baf-word-selected {
    background: none;
}
.gt-lc-mobile .gt-baf-word-selected .gt-baf-word-clickable, .gt-baf-word-clickable {
    color: #ddd;
}
.gt-lc-mobile .gt-baf-word-clickable:hover {
    background: #333;
}
.gt-lc-mobile div.gt-baf-translations {
    color: #777;
}
.gt-lc-mobile .gt-baf-hl {
    background: #333;
    color: #ddd;
}
.gt-cd-pos-pop {
    color: #777;
}
/*document*/
 .select-file-page .select-file-section {
    background: #181818;
}
.select-file-section .choose-document-prompt, .select-file-section .upload-filetypes-prompt {
    color: #ddd;
}
.select-file-page .select-file-section .button {
    background: #222;
}
.ft-icon-oval {
    background-color: #222;
}
.ft-icon-txt, body.displaying-saved-page .footer-icon-container.ft-icon-img-svd .ft-icon-txt, body.displaying-history-page .footer-icon-container.ft-icon-img-hst .ft-icon-txt {
    color: #ddd;
}
body.displaying-saved-page .footer-icon-container.ft-icon-img-svd .ft-icon-img-ctr .ft-icon-oval {
    background-image: url('https://ssl.gstatic.com/images/icons/material/system_gm/2x/star_grey600_48dp.png');
    background-color: #333;
    border-color: #333;
}
body.displaying-history-page .footer-icon-container.ft-icon-img-hst .ft-icon-img-ctr .ft-icon-oval {
    background-image: url('https://ssl.gstatic.com/images/icons/material/system_gm/2x/history_grey600_48dp.png');
    background-color: #333;
    border-color: #333;
}
/*the_color*/
 .gt-lc.gt-lc-mobile .gt-cd-pos, .sl-sugg .goog-inline-block.jfk-button-checked, .tl-sugg .goog-inline-block.jfk-button-checked {
    color: #777;
}
.goog-inline-block.jfk-button-checked:after {
    border-bottom: 3px solid #777;
}
.filled.gt-score-dot {
    background: #777;
}
.empty.gt-score-dot {
    background: #111;
}
/*HISTORY*/
 body.displaying-homepage:not(.with-lang-list), body.displaying-saved-page:not(.with-lang-list) .phrasebook-page, body.displaying-history-page:not(.with-lang-list) .history-page, history-body {
    outline: none;
}
.history-container, body.displaying-history-page .history-page {
    background: #1b1b1b;
}
.history-top-header {
    background: #181818;
}
.history-top-header .title, .history-container .empty-placeholder .placeholder-title {
    color: #ddd;
}
.history-top-header .close-button {
    filter: invert(1);
    outline: none;
}
.history-container .info-bar {
    background: #1b1b1b;
}
.history-container .info-bar, .history-container .history-features .delete-all-button {
    color: #777;
}
.history-container .language-pair {
    color: #464646;
    outline: none;
}
.history-entry .tl-input {
    color: #ddd;
}
.history-entry .tl-output {
    color: #777;
}
.history-entry.browsed, .history-entry:focus-within {
    background: #181818;
    outline: none;
}
.history-entry .browse-entry {
    outline: none;
}
/*PHRASEBOOK*/
 .phrasebook-container {
    background: #1b1b1b;
}
.phrasebook-top-header {
    background: #181818;
}
.phrasebook-top-header .title {
    color: #ddd;
}
.phrasebook-top-header .close-button {
    filter: invert(1);
    outline: none;
}
.phrasebook-container .search-bar {
    background: #181818;
}
.phrasebook-container .search-box {
    background: #222;
    color: #ddd;
}
.phrasebook-container .phrasebook-features .sort-button {
    outline: none;
    color: #777;
}
.goog-menu {
    background: #222;
    border: 1px solid #00000040;
}
.phrasebook-container .phrasebook-features .goog-option-selected {
    background: #2a2a2a;
    color: #ddd;
}
.phrasebook-container .phrasebook-features .goog-menu {
    color: #ddd;
}
.phrasebook-container .phrasebook-features .goog-menuitem-highlight, .phrasebook-container .phrasebook-features .goog-menuitem:hover {
    background: #303030;
}
.phrasebook-container .phrasebook-features .more-button, .phrasebook-container .phrasebook-features .export-button {
    outline: none;
}
.phrasebook-container .nav-bar {
    background: #1b1b1b;
}
.phrasebook-container .nav-bar .prev-button, .phrasebook-container .nav-bar .next-button {
    outline: none;
}
.phrasebook-container:not(.empty-search-query) .phrasebook-entry.browsed, .phrasebook-entry.focus-within, .phrasebook-entry:focus-within {
    background: #181818;
}
.phrasebook-entry .browse-entry {
    outline: none;
}
.phrasebook-container .nav-bar {
    color: #777;
}
.phrasebook-container .language-chip {
    outline: none;
    background: none;
    color: #777;
    border: 1px solid #333;
}
.phrasebook-container .language-chip:hover, .phrasebook-container .language-pair-container .language-chip.selected, .language-chip:focus, .language-chip:focus-within {
    background: #222;
    border-color: #333;
    color: #ddd;
}
.phrasebook-entry .tl-input {
    color: #ddd;
}
.phrasebook-entry .tl-output {
    color: #777;
}
.phrasebook-container .empty-placeholder .placeholder-title {
    color: #ddd;
}
.displaying-saved-page .phrasebook-page {
    background: #1b1b1b;
}
/*expoert to spreadsheet*/
 #heading {
    color: #ddd;
}
#message {
    color: #777;
}
#buttons .jfk-button-action {
    border: 1px solid #181818;
    outline: none;
}
#buttons .jfk-button-action:hover, #buttons .jfk-button-action:active {
    background: #444;
    outline: none;
}
.gender-promo {
    display: block;
}
.gender-promo-content {
    background: #333;
}
.gender-promo-title {
    color: #ddd;
}
.gender-promo-message {
    color: #aaa;
}
.gender-promo-learn-more {
    color: #fff;
}
.gender-promo::after {
    background: #333;
    box-shadow: none;
}
.gendered-translations .result-dict-wrapper:not(:last-of-type)::after {
    border-bottom: 1px solid #00000040;
}
.gendered-translations-learn-more {
    color: #777!important;
}
.gt-baf-previous-word-mobile {
    color: #ddd;
}
.frame .feedback-link a, .frame .feedback-link a:visited {
    color: #777!important;
}


/*other stuff*/

.gt-cc-t,
.gt-lc.gt-lc-mobile.show-as-one-card {
    background: #1b1b1b;
}
.gt-lc.gt-lc-mobile.show-as-one-card {
    border: 1px solid #00000040;
}
.gt-lc.gt-lc-mobile.show-as-one-card .gt-cc {
    border-top: 1px solid #00000040;
}
.gt-lc.show-as-one-card .gt-cc-l {
    border-left: 1px solid #00000040;
}
.gt-cc-t .gt-ct-text {
    color: #999;
}
.gt-cc-t .gt-cc-tc .gt-ct-tts.trans-listen-button .jfk-button-img, 
.gt-cc-t .gt-cc-bc .big-clear-button .jfk-button-img {
    filter: invert(1);
}

.cp-promo {
    background: #1b1b1b;
}
a:link .cp-promo-title, a:visited .cp-promo-title {
    color: #ddd;
}
a:link .cp-promo-subtext, a:visited .cp-promo-subtext {
    color: #777;
}
.ita-ppe-box {
    background-color: #222;
    border: 1px solid #00000040;
    color: #ddd;
}
.ita-ppe-hlt {
    background: #333;
    color: #ddd;
}
.gt-lc.gt-lc-mobile .gt-rw-div .gt-cd-cl {
    color: #777;
}
.gt-lc.show-as-one-card .gt-cc-r {
    border-right: none;
}
.gt-related-suggest-message {
    background-color: #181818;
}
.notification-area {
    display: none;
}
}
