style.css:
@-moz-document domain("subscene.com") {
body {
    background: #0A0A0A;
    color: #CCC;
    border-bottom: none;
}

#user-navigation, #forum-navigation, #admin-navigation {
    float: right;
    background: #101010;
    line-height: 26px;
    height: 30px;
    padding: 0 11px;
    border-radius: 0 0 4px 4px;
    -moz-border-radius: 0 0 4px 4px;
    -webkit-border-radius: 0 0 4px 4px;
}

.box {
    border-top: solid 1px #101010;
    padding: 0;
    padding: 11px 0;
    text-align: left;
}

input {
    background-color: #101010;
    -webkit-rtl-ordering: logical;
    cursor: text;
    padding: 1px;
    border-width: 2px;
    border-style: inset;
    border-color: initial;
    border-image: initial;
}

#search .text {
    color: #FFF;
    border-style: solid;
    border-width: 1px;
    border-color: #101010 #101010 #101010 #101010;
    border-radius: 15px;
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    width: 450px;
    height: 18px;
    padding: 6px 0 4px 4px;
    margin-right: 6px;
    margin-top: -2px;
    font-size: 15px;
}

button, .button {
    font-size: 14px;
    color: #CCC;
    padding: 6px 10px;
    background: #151515;
    border-radius: 15px;
    -moz-border-radius: 3px;
    -webkit-border-radius: 3px;
    border: 0;
}

button:hover, .button:hover {
    background: #151515;
}

.content {
    border-top: 1px solid #101010;
    padding: 14px 0 22px 0;
}

footer {
    background: #0A0A0A;
    height: 39px;
    clear: both;
    border-radius: 4px;
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    margin: 200px auto -110px;
    padding: 13px;
    font-size: 12px;
}

.sustain {
    border: none;
    border-radius: 4px;
    -moz-border-radius: 4px;
    -webkit-border-radius: 4px;
    padding: 13px;
    margin: 60px -13px;
    line-height: 24px;
}

a:hover {
    color: #00599e;
}

#messageCount.badge {
    min-width: 20px;
    background-color: #0F0F0F;
}

.byFilm .language-filter span {
    font-size: 18px;
    font-weight: bold;
    color: #E0245E;
    font-family: "Century Gothic",Arial;
}

.byFilm .sort-by {
    float: right;
    font-size: 13px;
    height: 30px;
    line-height: 30px;
    background-color: #0A0A0A;
    border: none;
    border-bottom: 0;
    color: #E0245E;
    padding: 0 8px 0 6px;
    border-radius: 4px 4px 0 0;
}

.subtitles table {
    background-color: #0F0F0F;
    width: 100%;
}

.subtitles td.a1 span {
    background-color: #0F0F0F;
    color: #CCC;
}

.subtitles td.a1 a:link {
    display: block;
    background-color: #0F0F0F;
}

.subtitles td.a1 a:visited, .subtitles td.a1 .visited {
    background-color: #0F0F0F;
}

.subtitles td a {
    color: #008860;
    outline: 0 none;
}

.subtitles thead {
    background: #008860;
    color: #FEFEFE;
    height: 21px;
    line-height: 21px;
}

.subtitles td.a1 a:visited, .subtitles td.a1 .visited {
    background-color: #E0245E;
}

.bread a, .bread a:visited {
    color: #008860;
}

.subtitle .comment {
    box-shadow: none;
    padding: 11px 9px;
    color: #CCC;
    min-height: 40px;
    position: relative;
    background: #101010;
    margin: 28px 0 8px 0;
    line-height: 16px;
}

.subtitle .comment:before {
    background: none;
    display: block;
    height: 27px;
    width: 27px;
    left: 10px;
    top: -20px;
    position: absolute;
    content: "";
}

.window {
    background: #101010;
    border: none;
    padding: 11px 13px;
    margin: 0 0 13px 0;
    clear: both;
}

.posts .post {
    float: right;
    width: 500px;
    margin-right: 0;
    box-shadow: none;
    padding: 8px 11px;
    color: #CCC;
    min-height: 80px;
    background: #101010;
    position: relative;
    word-wrap: break-word;
}

.posts .post:before {
    background: none;
    display: block;
    height: 24px;
    left: -21px;
    position: absolute;
    top: 52px;
    width: 34px;
    content: "";
}

.user-home .subtitles .list {
    background-color: #0F0F0F;
    border-radius: 4px 4px 4px 4px;
    margin-top: 6px;
    padding: 1px 3px;
    width: 532px;
}

.user-home .subtitles ul>li, .user-home .footer {
    float: left;
    width: 173px;
    text-align: center;
    height: 240px;
    margin-bottom: 56px;
    margin: 4px 3px 0 4px;
    padding-bottom: 5px;
    height: 300px;
    padding-top: 1px;
    background-color: #0F0F0F;
    border: none;
    border-radius: 5px;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    width: 170px;
    position: relative;
}

.pagination>li>a, .pagination>li>span {
    background: #0F0F0F;
}

.pagination>li>a, .pagination>li>span {
    position: relative;
    float: left;
    padding: 6px 12px;
    margin-left: -1px;
    line-height: 1.428571429;
    text-decoration: none;
    background: none;
    border: none;
}

.pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus {
    color: #fff;
    background-color: #1888;
    border: none;
}

.pagination>li>a:hover, .pagination>li>span:hover, .pagination>li>a:focus, .pagination>li>span:focus {
    background: none;
}

.byTitle .search-result {
    background-color: #0F0F0F;
    border-radius: 4px;
    margin-top: 8px;
    padding: 1px 5px;
}

a, a:link {
    color: #1d4960;
    text-decoration: none;
    cursor: pointer;
}

#search .text {
    border: none;
    background-color: #101010;
    color: #CCC;
}
}
