style.css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweetdeck.twitter.com") {
/* BorderDeck amoethyst is a theme for TweetDeck created by amoethyst

To close the picture preview just move your mouse to the top and click
when a pointer appears.

To tweet use ctrl+enter (don't wait for a "Tweet" button to appear)
*/

	/* Font */

/* Source Sans Pro */
@font-face {
  font-family: 'Source Sans Pro';
  font-style: normal;
  font-weight: 400;
  src: local('Source Sans Pro'), local('SourceSansPro-Regular'), url(https://fonts.gstatic.com/s/sourcesanspro/v9/ODelI1aHBYDBqgeIAH2zlNV_2ngZ8dMf8fLgjYEouxg.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
/* bold */
@font-face {
  font-family: 'Source Sans Pro';
  font-style: normal;
  font-weight: 700;
  src: local('Source Sans Pro Bold'), local('SourceSansPro-Bold'), url(https://fonts.gstatic.com/s/sourcesanspro/v9/toadOcfmlt9b38dHJxOBGEo0As1BFRXtCDhS66znb_k.woff2) format('woff2');
  unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}

html {
    color: #c1c1c1;
    font-family: Source Sans Pro, sans-serif !important;
    font-weight: 400;
}

html.dark .list-account,
.list-group li *,
.list-account .username {
	text-shadow: none;
    color: #d1d1d1;
}

.os-windows {
    font-family: Source Sans Pro, sans-serif !important;
    font-weight: 400;
}
html.dark {
    color: #c1c1c1;
    font-family: Source Sans Pro, sans-serif !important;
    font-weight: 400;
}
.tweet-detail .tweet-translation-text {
    font-family: Source Sans Pro, sans-serif !important;
}
.txt-mute {
    color: #b7b7b7 !important;
    opacity: 0.9 !important;
}

    /* scrollbar */

::-webkit-scrollbar-thumb:vertical {
    border-radius: 0 !important;
    box-shadow: inset 8px 0 #b7b7b7 !important;
    transition: 0.5s !important;
}
::-webkit-scrollbar-thumb:horizontal {
    border-radius: 0 !important;
    box-shadow: inset 0 8px #b7b7b7 !important;
    transition: 0.5s !important;
}
::-webkit-scrollbar-thumb:vertical:hover {
    box-shadow: inset 8px 0 #474747 !important;
    transition: 0.5s
}
::-webkit-scrollbar-thumb:horizontal:hover {
    box-shadow: inset 0 8px #474747 !important;
    transition: 0.5s !important;
}
::-webkit-scrollbar-track {
    border: 0 !important;
}
::-webkit-scrollbar:vertical {
    width: 8px !important;
}
::-webkit-scrollbar:horizontal {
    height: 8px !important;
}
.scroll-styled-v::-webkit-scrollbar-track, .scroll-styled-h::-webkit-scrollbar-track {
    border: 0;
}

    /* Selection color */

::-moz-selection { color: #fff; background-color: #b7b7b7;}
::selection { color: #fff; background-color: #b7b7b7;}

    /*  Links */

a {
    color: #b7b7b7 !important;
    text-decoration: none !important;
    transition: 250ms;
}
a:hover, a:active {
    color: #474747 !important;
    outline: none !important;
    transition: 250ms !important;
}
a:focus {
    color: #b7b7b7;
    transition: 250ms !important;
    outline: none !important;
}
.link-complex .link-complex-target {
    text-decoration: none !important;
    color: inherit;
}
.prf-header .prf-siteurl, .prf-header .prf-bio a, .prf-header
.pretty-link {
    color: #fff !important;
}
.prf-header .prf-siteurl:hover, .prf-header .prf-bio a:hover,
.prf-header .pretty-link:hover {
    text-decoration: underline;
}

    /* App header (aka "that thing on the left") */

html.dark .social-proof-container,
html.dark .prf-meta,
.app-header, .column-nav-item, .app-navigator, .app-title {
    background-color: #1c1c1c !important;
}

html.dark .bg-color-twitter-white {
	background-color: #3f3f3f;   
}

html.dark .dropdown-menu .is-selected {
	background-color: #151515;
    color: #f0f0f0;
}

.app-header {
    border-right: 1px solid #b7b7b7;
}
.app-navigator {
    margin-bottom: 0 !important;
}
.column-nav-link, .app-nav-link, .app-nav-tab {
    color: #aaa !important;
}
.app-search-input, .app-search-fake, .btn-compose {
    background-color: transparent !important;
    box-shadow: none !important;
    transition: 0.5s !important;
}
.app-search-fake {
    color: #aaa;
    border: none;
}
.search-input-control {
    background-color: #1c1c1c;
}
.js-popover-content {
	background-color: #1c1c1c;
}
.app-search-input:focus, .app-search-input.is-focused {
    border: 1px solid #98BC37;
}
.btn-compose {
    color: #b7b7b7;
}
.app-search-fake:hover, .btn-compose:hover {
    color: #474747;
}
.with-nav-border-t::before {
    border-top: 0 !important;
}
.antiscroll-scrollbar {
    background-color: #b7b7b7 !important;
    border-radius: 0;
}
.column-nav-updates {
    color: #474747;
}
.numbered-badge {
    border-radius: 100%;
    border: 1px solid #b7b7b7;
    background-color: #1c1c1c;
}
.nav-user-info {
  margin: 0 !important;
}
.nav-user-info .tweet-avatar {
  box-shadow: 0 0 2px 1px #b7b7b7;
  float: none !important;
  margin: 4px 7px 0 7px !important;
}
.txt-seamful-yellow {
	color: #b7b7b7;
}
.update-available-item {
    background-color: #b7b7b7;
    color: #fff !important;
}
.update-available-item:hover {
    background-color: #474747;
    color: #fff !important;
}
.update-available-item::before {
	width: 0px;
}
.icon-medium {
	font-size: 24px !important;
}
.txt-twitter-yellow {
    color: #b7b7b7;
    transition: 250ms !important;
}
.txt-twitter-yellow:hover {
    color: #474747;
}

    /* Twitter logo */

.sprite-logo-badge, .sprite-logo {
    background: transparent;
}
.app-title:before {
    left: 10px;
    content: "\f014";
    font-family: "tweetdeckregular";
    font-size: 27px;
    color: #b7b7b7;
}

    /* Search Popup */

.popover {
    background-color: #1c1c1c;
    box-shadow: none;
    border-radius: 0;
}
.is-condensed .popover-position-rt {
    left: 47px;
}
input, textarea, select, .uneditable-input {
    border-radius: 0;
    background-color: transparent;
    color: #474747;
    border: none;
    border-bottom: 2px solid #b7b7b7;
    transition: 250ms !important;
}
input:focus, textarea:focus, select:focus, .uneditable-input:focus {
    border-bottom: 2px solid #474747;
}
.list-item {
    background-color: transparent !important;
    color: #b7b7b7 !important;
}
.list-placeholder {
    text-shadow: none;
}
.icon-medium {
    font-size: 18px;
}
input:focus, select:focus, textarea:focus, .focus {
    border-color: transparent;
    box-shadow: none;
}
.caret-inner {
    border-bottom: 6px solid #b7b7b7;
}
.facet-type {
    border-bottom: none !important;
    background-color: transparent !important;
}
.accordion-divider-t {
    border-top: none !important;
    border-bottom: none !important;
}
.facet-subtitle {
    color: inherit;
}
.facet-title {
    transition: 250ms !important;
}
.accordion-header {
    padding: 9px 5px 9px 0 !important;
    color: #b7b7b7 !important;
}
.accordion-header:hover {
    color: #474747 !important;
}
.accordion .icon-arrow-d, .accordion .icon-arrow-u {
    color: #b7b7b7 !important;
}
.accordion .icon-arrow-d:hover, .accordion .icon-arrow-u:hover {
    color: #474747 !important;
}
.list-item:hover, .list-item:active, .list-item.is-selected {
    color: #474747 !important;
}
.list-item:hover .txt-mute, .list-item:active .txt-mute,
.list-item.is-selected .txt-mute {
    color: inherit !important;
}
.list-item .list-icon {
    color: inherit !important;
}
.account-summary:hover {
    color: #474747;
}
.txt-r-text-link {
	color: #b7b7b7;
}
.txt-r-text-link:hover {
	color: #474747;
}

    /* Columns */

.app-columns-container, .column, .app-content {
    background-color: #1c1c1c !important;
}
.app-columns {
    padding: 0 !important;
}
.column {
    margin: 0 !important;
    border: 0;
    border-right: 1px solid #b7b7b7 !important;
}
.app-columns > .draggable-dragging {
    border-left: 1px solid #b7b7b7;
}
.needs-scroll-bottom-offset.scroll-styled-h {
    bottom: 0 !important;
}

    /* Columns header */

.column-header, html.dark .column-header {
    background-color: #1c1c1c !important;
    border-bottom: 1px solid #b7b7b7 !important;
    color: #b7b7b7;
}
.column-header-temp, html.dark .column-header-temp {
    background-color: #1c1c1c !important;
    border: 0 !important;
    color: #b7b7b7;
}
.column-type-icon, html.dark .column-type-icon {
    display: none;
}
.sprite-drag-vertical, html.dark .sprite-drag-vertical {
    background: transparent;
    height: 100% !important;
    width: 10px !important;
    transition: 250ms !important;
}
.sprite-drag-vertical:hover {
    background: #b7b7b7;
    opacity: 0.5;
}
.column-drag-handle, html.dark .column-drag-handle {
    left: 0;
    top: 0;
}
.column-number {
    display: none;
}
.column.is-focused {
    box-shadow: 0 0 6px 3px #b7b7b7;
    z-index: 199;
}
.is-new .column-header, .is-new .column-header-temp {
    box-shadow: inset 0 -2px #b7b7b7;
    transition: 0.25s;
}
.more-tweets-glow {
    background: none;
}
.column-message {
    background-color: #1c1c1c;
}
.facet-content, .facet-user {
    color: #b7b7b7;
}
.column-title-edit-box {
    background-color: #1c1c1c !important;
    color: #b7b7b7 !important;
    border-bottom: none !important;
    border-radius: 0px;
}

    /* Smaller columns header */

.column-header, .column-header-temp, html.dark .column-header, html.dark .column-header-temp {
    height: 40px;
    line-height: 40px;
}
.column-content {
    top: 0 !important;
}
.column-title-back .icon, html.dark .column-title-back .icon {
    top: 20px;
}
.column-title-back {
    color: #b7b7b7;
    transition: 250ms;
}
.column-title-back:hover {
    color: #474747;
}
.column-title-back .username {
	color: inherit !important;
}

.column-header .column-title, html.dark .column-header .column-title {
    margin-left: 0;
}

    /* Fix for BTD */

.btd-ready .column-title .column-head-title, .btd-ready .column-title
.attribution {
    display: inline;
}

    /* Columns settings */

.column-settings-link, .facet-type.is-expanded {
    background-color: transparent !important;
    border: 0 !important;
}
.column-options, .column-options .button-tray {
    background-color: #1c1c1c !important;
}
.accordion-divider-t, .facet-type {
    border: 0;
}
.facet-subtitle {
    color: #b7b7b7;
}
.facet-subtitle:hover {
    color: #474747;
}
.with-column-divider-bottom {
    border-bottom: 1px solid #b7b7b7;
}
.btn-options-tray, .btn-neutral .label {
    transition: 250ms !important;
}
.btn-options-tray:hover, .btn-options-tray:focus, .btn-neutral:hover
.label {
    color: #474747;
    text-decoration: none !important;
}
select {
    -moz-appearance: none;
    -webkit-appearance: none;
    appearance: none;
    background: #1c1c1c;
    border: 0;
    border-bottom: 2px solid #b7b7b7;
    border-radius: 0;
}
select:hover, select:focus {
	  border-bottom: 2px solid #474747;
	  cursor: pointer;
}
input:focus, select:focus, textarea:focus, .focus {
    box-shadow: none;
    border-color: #474747
}
select:-moz-focusring {
    color: transparent;
    text-shadow: 0 0 0 #b7b7b7;
}

.icon-mention-color, .icon-retweet-color, .icon-image-color,
html.dark .icon-mention-color, html.dark .icon-retweet-color, html.dark .icon-image-color {
    color: #b7b7b7;
}

    /* Add column popup */

.column-type-icon {
    color: #b7b7b7;
    transition: 250ms !important;
}
.btn > span {
    transition: 250ms !important;
}
.btn:hover > .column-type-icon {
    color: #474747;
}
.btn:hover > span {
    color: #474747;
}
.s-minimal .mdl-header, .lst-launcher .top-row {
    border-bottom-color: #b7b7b7;
}

    /* Tweets */
html.dark .is-inverted-dark,
html.dark .is-inverted-dark .stream-item,
.stream-item, html.dark .stream-item {
    border-bottom: 0;
    background-color: #1c1c1c;
    color: #c1c1c1;
}
.tweet-footer .pull-left > span {
    display:none;
}
.item-box, html.dark .item-box {
    padding-bottom: 4px;
    padding-top: 4px;
}
.scroll-conversation, .tweet-detail-wrapper {
    background-color: #1c1c1c !important;
}

.tweet-detail-wrapper, .tweet-stats, .tweet-detail-actions,
.detail-view-inline, .card-holder, html.dark .tweet-detail-wrapper,
html.dark .tweet-stats, html.dark .tweet-detail-actions,
html.dark .detail-view-inline, html.dark .card-holder {
    border: 0;
    margin-top: 4px;
}
.tweet-stat {
    color: #b7b7b7;
}
.tweet-stat:hover {
    color: #474747;
}
.tweet-stat[data-type="repliers"] {
    color: #b7b7b7;
}
.tweet-stat * {
	color: inherit !important;
}
a:hover .stat-word {
	color: inherit;
}
.tweet-stats > li {
	color: #b7b7b7;
}
.tweet-stats > li[data-type="favoriters"] a:hover,
.tweet-stats > li[data-type="retweeters"] a:hover {
	color: #474747 !important;
}
.bing-logo:before {
    content: "Bing";
}
.bing-logo {
    background: none !important;
    height: 14px;
}
.quoted-tweet {
    border: 0;
    border-left: 2px solid #b7b7b7;
	border-radius: 0;
}
.media-sensitive {
  background-color: #1c1c1c;
  color: #fff;
}
.media-sensitive-content {
  border: 2px solid #b7b7b7;
}
.media-sensitive-title {
  color: #c1c1c1;
}
.in-tweet-divider::before {
    display: none;
}
.in-tweet-divider {
    padding: 0;
}
.tweet-action-item *, .tweet-detail-action-item * {
    color: #aaa;
    transition: 250ms !important;
}
.tweet-action-item *:hover, .tweet-detail-action-item *:hover {
    color: #474747;
}
.pull-right.icon-retweet {
    color: #b7b7b7 !important;
}
.social-proof-for-tweet-title {
    background-color: #1c1c1c !important;
    border-bottom: 0 !important;
    color: #b7b7b7 !important;
}
.quoted-tweet {
    color: #c1c1c1;
}
.modal-content {
	background-color: #1c1c1c;
}
.txt-twitter-black {
	color: #c1c1c1;
}
.mdl-header-divider {
	border-bottom-color: #b7b7b7;
}
.thread {
	margin-top: 32px;
	border-radius: 0;
	background-color: #b7b7b7;
	height: 100%;
}
html.dark .thread {
    background-color: #b7b7b7;
}
.show-more-thread, .dot-thread {
    display: none;
}
.conversation-more {
    color: #b7b7b7;
    transition: 250ms !important;
}
.conversation-more:hover {
    color: #474747;
    text-decoration: none;
}
.btn-neutral-positive {
    color: #b7b7b7;
    transition: 250ms !important;
}
.btn-neutral-positive:hover {
    color: #474747;
}

	/* Links to account */

.account-link * {
    color: #b7b7b7;
}
.account-link .link-complex-target, .account-link .username, .account-inline .icon-protected {
    transition: 250ms;
}
.account-link:hover * {
    color: #474747 !important;
}
.nbfc .account-link {
    color: #b7b7b7 !important;
}
.nbfc .account-link:hover {
    color: #474747 !important;
}
.drawer .nbfc {
    color: #b7b7b7;
    transition: 250ms !important;
}
.drawer .nbfc:hover {
    color: #474747;
}

img.emoji {
	box-shadow: none !important;
}

	/* Trends column */

.trend-label {
	color: #b7b7b7;
}
.trend-item .trend-item-stats {
	color: inherit;
}

	/* Rounded Avatars */

.tweet-avatar, .size24, .size72, .prf-img, .accs .acc-avatar {
    border-radius: 100%;
}
.tweet-avatar {
    margin: 2px !important;
}
.avatar {
    border-radius: 100%;
    background-color: #fff;
    background-clip: padding-box !important;
}
.compose-account .compose-account-img {
    transition: opacity 250ms !important
}
.is-selected.compose-account .compose-account-img {
    box-shadow: 0 0 0 2px #b7b7b7 !important;
}
.compose-account:focus .compose-account-img {
    box-shadow: none;
}
.list-account img {
    box-shadow: 0 0 0 1px #b7b7b7;
}
.item-img {
    width: 52px;
}

    /* RT fav tweet */

.tweet-dogear {
    background-image: none;
    width: auto;
    height: 100%;
}
.avatar {
    transition: box-shadow 250ms !important;
}
.tweet-header .avatar, .tweet-detail .avatar, .account-summary .avatar {
    box-shadow: 0 0 0 2px #b7b7b7;
}
.tweet-header:hover .avatar, .account-summary .avatar:hover {
    box-shadow: 0 0 0 2px #474747 !important;
}
.is-favorite .avatar {
    box-shadow: 0 0 0 2px #fa3;
}
.is-retweet.is-favorite .avatar {
    box-shadow: 0 0 0 2px #fff;
}
.is-retweet .avatar {
    box-shadow: 0 0 0 2px #7b5;
}
.avatar.size24 {
    box-shadow: 0 0 0 1px #b7b7b7;
}
.avatar.size24:hover {
    box-shadow: 0 0 0 1px #474747;
}
.tweet-header:hover .size24 {
    box-shadow: 0 0 0 1px !important;
}
.is-retweet .tweet-timestamp, .is-favorite .tweet-timestamp {
    margin-right: 0;
}

    /* Compose panel */

.compose {
    background-color: #1c1c1c !important;
    color: #b7b7b7;
    border-right: 1px solid #b7b7b7;
}
.compose-header {
    border: 0;
}
.compose-header .icon-close {
    display: none;
}
.compose-text-title {
    color: #b7b7b7;
}
.txt-r-white {
    color: inherit !important;
}
.compose-account {
    background-color: transparent;
}
.compose-account-selected {
    background: transparent;
}
.compose-account-selected .icon {
    display: none;
}
.js-compose-message-header {
	display: none;
}
.compose-text-container {
    background-color: #2c2c2c !important;
    border-radius: 0 !important;
}
.compose-text {
	background-color: transparent !important;
	color: #fff;
	border: 0 !important;
}
.compose-text-container {
	border-top: 2px solid #b7b7b7 !important;
}
.compose .compose-text-container, .compose-text {
    background-color: #2c2c2c !important;
}
.compose-text-container:focus, .compose-text:focus {
    background-color: #2c2c2c !important;
}
.compose-text-container:focus {
	border-top-color: #474747 !important;
}
.js-quote-tweet-holder, .compose-media-bar-holder {
    background-color: #2c2c2c !important;
}
.js-quote-tweet-holder .nbfc {
	color: inherit !important;
}
.compose-media-bar-holder {
    padding-bottom: 0 !important;
}
.spinner-button-container {
    display: none;
}
.character-count-compose:disabled {
    color: #b7b7b7
}
.btn-on-blue {
    background-color: transparent !important;
    color: #b7b7b7 !important;
    transition: 250ms;
    box-shadow: none !important;
}
.btn-on-blue:hover {
    color: #474747 !important;
}
.btn-on-blue[disabled], .btn-on-blue.is-disabled {
    color: #01000c !important;
}
.compose-message-recipient-input-container {
    box-shadow: none !important;
}
.compose-message-recipient {
    border-color: #b7b7b7;
}
.compose-reply-tweet {
    background: #2c2c2c;
    color: #aaa;
}
.compose-reply-tweet .fullname {
	color: #b7b7b7 !important;
}
.replyto-caret {
    display: none;
}
.compose-reply-tweet .account-link:hover .fullname {
    color: #474747 !important;
}
.compose-remember-state {
    color: #b7b7b7;
}

input:focus {
    box-shadow: none !important;
    color: #c4c4c4;
}
.compose-reply-tweet .avatar {
    margin: 1px;
}
.js-quote-tweet-holder .tweet-avatar {
