}
.tabela6 TH{
    background-color: rgb(0,0,0) !important;
}
.tabela4 .naglowek4 th{
    color: #d9d9d9;
}
.tabela4 th {
    background-color: #000 !important;
}
.tabela4 tr .r{
    background-color: #000 !important;
}
#tabeleczka tr .r, #tabeleczka #naglowektabeleczki th.r{
    background-color: #000 !important;
