}
.switchart a{
    color: #d9d9d9 !important;
}
.offer_top .offer_price{
    color: #b5b5b5;
}
.box .main {
    background: #131313 repeat-y;
}
.narrow .box .main,
.small .main {
    background: #131313 repeat-y;
}
.narrow .box .footer,
.small .footer {
    background: none;
}
.narrow .box .agdlab .main,
.small .agdlab .main {
    background: #131313 repeat-y;
}
.narrow .box .agdlab .footer,
.small .agdlab .footer {
    background: none;
}
.narrow .box .main .forum li,
.narrow .box .main .files li,
.small .main .forum li,
.small .main .files li {
    background: #0c0c0c;
}
.narrow .box .main .files li.even,
.narrow .box .main .forum li.even,
.small .main .files li.even,
.small .main .forum li.even {
    background: #161616;
}
.box .main .preview .text {
    color: #d9d9d9;
}
.box .notabs .pane {
    background: none;
}
.box .main .description{
    color: #a8a8a8;
}

#container {
    background: none;
    background-color: #0c0c0c;
}
#contenttabwrapper {
    background: none;
    background-color: #0c0c0c;
    filter: drop-shadow(5px 5px 15px black);
}
.narrow .box .header,
.small .header {
    background: url(https://i.imgur.com/6sJXkGD.jpg) no-repeat
}
.box .header .options a.close {
    background: url(https://i.imgur.com/4ZV2By8.png) 0px 0 no-repeat;
    margin: 2px;
}
.box .header .nav div {
    background: url(https://www.pclab.pl/img/agdlab_nav_less_2.gif) no-repeat
}
.box .header {
    background: url(https://i.imgur.com/7uZsa5D.jpg) no-repeat
}
.box .footer {
    background: none;
}
.staticfiles .header {
    background: url(https://i.imgur.com/Ssxg0Yo.png) no-repeat;
}
.special .main .list {
    background-color: #0c0c0c
}
.special .footer {
    background: none;
}
.staticfiles .main .list {
    background-color: #0c0c0c
}
.staticfiles .main .more {
    background-color: #0c0c0c
}
.staticfiles .footer {
    background: none;
}
.box .main .tabs {
    background: #0c0c0c repeat-x
}
.content .header {
    background: url(https://i.imgur.com/Ssxg0Yo.png) no-repeat;
    background-size: 606px 28px;
}
.content .tools {
    background-color: #131313;
}
#content #main {
    filter: drop-shadow(5px 5px 15px black);
}
.content .main .options {
    background: none;
    background-color: #131313;
}
.special .main .preview {
    background-color: #131313;
}
.content .list .first {
    background-color: #131313;
}
.content .list .element {
    background-color: #131313;
}
.content .substance {
    background-color: #131313;
}
.content .substance .ledge {
    background: #131313; /*https://imgur.com/YQlvqtx*/
}
.content .tools ul {
background: url(https://i.imgur.com/J1sHcE8.png) 0 0 no-repeat
}
.content .comments .header {
    background: #131313 
}
.content .comments .footer {
    background: #0c0c0c;
}
.content .comments .post {
    background: #0c0c0c;
}
.content .post .field .up {
    background: none;
}
.content .post .field .middle {
    background: none;
}
.content .post .field textarea {
    background-color: #333333;
    color: white;
}
.content .post .field .down {
    background: none;
}
.content .main .pages {
    background-color: #0c0c0c;
    background: none;
}
.special .first .title {
    background: none;
}
#footercontainer {
    background: none;
}
.content .substance .navigation {
    background: none;
}
.special .preview .text {
    color: #d9d9d9;
}
.news .list .element .text {
    color: #d9d9d9;
}
.article .list .element .text {
    color: #d9d9d9;
}
.article .chapters a{
    background: url(https://i.imgur.com/ph4Gx1p.png) no-repeat;
    color: #dadada;
}
.article .chapters table td{
    background: url(https://i.imgur.com/ZGTRsQV.jpg) repeat-x;
}
.article .chapters td.fill{
    background: url(https://i.imgur.com/ZGTRsQV.jpg) repeat-x;
}
.article .chapters td.left{
    background: url(https://i.imgur.com/7EmJuGd.png) repeat-x;
}
.article .chapters td.right{
    background: url(https://i.imgur.com/TXwnAhS.png) repeat-x;
}
.content .substance .data {
    color: #d9d9d9;
}
.special .preview .title a {
    color: #6ea0ff;
}
.news .list .element .title a {
    color: #6ea0ff;
}
.article .list .element .title a {
    color: #6ea0ff;
}
.content .substance .title a {
    color: #6ea0ff;
}
.special .text .info a {
    color: #ccc;
}
.special .text .comments {
    background: url(https://i.imgur.com/D45Ark4.png) 0 2px no-repeat
}
.special .text .views {
    background: url(https://i.imgur.com/j31KSn1.png) 0 0px no-repeat
}
.news .list .element .info a.unread {
    color: #ccc;
}
.article .list .element .info a.unread {
    color: #ccc;
}
.content .tools .right {
    background: none;
}
.content .tools .middle {
    background: url(https://i.imgur.com/n8q52YN.png) repeat-x
}
.content .tools .left {
    background: none;
}
.content .comments .tab {
    background: url(https://i.imgur.com/Xz4nMNM.png) no-repeat
}
.content .comments .tab span {
    background: url(https://i.imgur.com/Xz4nMNM.png) 100% 0 no-repeat
}
.box .main .preview .text span {
    background: url(https://i.imgur.com/FIOY0Zt.png) 0 2px no-repeat
}
.box .main .list li span {
    background: none;
}
.box .main .list li span a {
    background: url(https://i.imgur.com/FIOY0Zt.png) 0 2px no-repeat
}
.box .blog .main .item .info span {
	background: none;
}
.box .blog .main .item .info span a {
    background: url(https://i.imgur.com/FIOY0Zt.png) 0 2px no-repeat
}
.box .blog .main .item .info a {
	color: #898989;
}
#header .right .nav a.register{
    background: url(https://i.imgur.com/KgcvaGV.png) 0 1px no-repeat
}
.narrow .box .main .more a, .small .main .more a {
	background: url(https://i.imgur.com/KgcvaGV.png) 0 1px no-repeat
}
.box .main .more a {
	background: url(https://i.imgur.com/KgcvaGV.png) 0 2px no-repeat
}
.content .substance .navigation .next .link a{
    background: url(https://i.imgur.com/KgcvaGV.png) right 3px no-repeat
}
.content .substance .navigation .previous .link a{
    background: url(https://i.imgur.com/8lhKbA9.png) 0 3px no-repeat
}
.ps_item .ps_business .ps_price{
    color: #d9d9d9
}
.content .comments .odd {
    background-color: #0c0c0c;
}
