    background-color: #161616;
}
.content .comments .comment {
    color: #d9d9d9;
}
.content .comment .quotemain {
    border: 1px solid #000000;
    background-color: #3e4a5a
}
.content .comments .center {
    background: #0c0c0c;
}
#partner a {
    filter: grayscale(100%);
}
#tabwrapper img {
    filter: grayscale(100%);
}
#header .left .menu {
    filter: saturate(50%);
}
#header .right {
    filter: saturate(50%)
}
#footer {
    filter: saturate(50%)
}
#footermirror {
    filter: saturate(50%)
}
.article .toc .header {
    background: none;
}
