    color: #d9d9d9;
}
.article .intro .footer{
    background: #212121 repeat-y;
}
.content .comments .more{
    background: none 0 0 repeat-x;
}
.article .toc .footer {
    background: none;
}
.article .toc .center {
background: url(https://i.imgur.com/S05nIbN.jpg) repeat-y
}
.article .toc li {
	background: none;
}
.article .toc .tab {
	background: url(https://i.imgur.com/UBd1uMP.png) no-repeat
}
.article .toc .tab span {
	background: url(https://i.imgur.com/UBd1uMP.png) 100% 0 no-repeat
}
img[src*="/img/logo.png"] {
    width: 0;
    height: 100%;
    padding-right: 250px;
    background: transparent url(http://forum.pclab.pl/public/style_images/4_logo.png) top left no-repeat;
}
img[src*="/img/tab_out.gif"] {
    width: 0;
    height: 100%;
    padding-right: 19px;
    background: transparent url(https://i.imgur.com/eBAoZVN.png) top left no-repeat;
}
#tabwrapper #tabin,
#tabwrapper #tabout {
    background: none;
}
img[src*="/img/tab_in.gif"] {
    width: 0;
    height: 100%;
    padding-right: 19px;
    background: transparent url(https://i.imgur.com/1p23nai.png) no-repeat;
}
#tabwrapper #tabin {
    background: none;
}
.ps_item{
    border-radius: 0;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    border: 1px solid #d9d9d9;
}
.ps_item .ps_business{
    border-top: 1px solid #d9d9d9;    
}
.ps_item .ps_title{
    border: 1px solid #d9d9d9;
    border-radius: 0;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    background: #0e0e0e;
}
.ps_item .ps_offers span{
    border: 1px solid #d9d9d9;
    border-radius: 0;
    -moz-border-radius: 0;
    -webkit-border-radius: 0;
    background: #0e0e0e;
    color: #d9d9d9;
}
.ps_item .ps_offer .ps_price{
    color: #d9d9d9;
}
