style.css:
@-moz-document domain("justdancenow.com")
{
/*------------Menu--------------*/
#just-dance-now {
	background: url(https://i.imgur.com/EQBhxQo.png) center/cover no-repeat;
	background-size: 100% 100%!important;
	background-size: cover;
	background-repeat: no-repeat;
	background-position: center
}
#song-selection
{
    background-image: url(https://i.imgur.com/XpATIXA.png);
    background-size: cover;
}
.song--focused {
	transform: scale(1.05, 1.05);
	top: -3%;
	z-index: 9999999999999999999999999999999999999999!important;
	left: 0%;
	opacity: 1!important
}
.song__cover {
	transform: scale(0.85, 0.85);
}
.song__cover {
	width: 115%;
	-webkit-mask-image: url(https://i.imgur.com/zPUEwlK.png);
	-webkit-mask-size: 103%;
	-webkit-mask-position: center;
	-webkit-mask-repeat: no-repeat;
	top: -4%;
	left: -6.5%
}
.song--focused .song__decoration::after {
	background-image: url(https://i.imgur.com/J2QXM3W.png);
	z-index: 9999999;
	width: 94.5%!important;
	height: 94.5%!important;
	left: 4%;
	top: 5%
}
.song--focused img.song__cover {
    animation: focusZoom 3s infinite;
}
.item-container.item-container--y.favorites .item-container.item-container--x .song--focused::before {
    top: -88% !important;
    left: -15% !important;
    transform: rotate(-16deg) scale(1) !important;
}
.item-container.item-container--y.favorites .item-container.item-container--x .song::before {
    content: url(https://dl.dropbox.com/s/9bxkhmp3lzn2pni/Overlay_Favorite.png?dl=0);
    position: absolute;
    z-index: 9999;
    top: -25%;
    left: -15%;
    transform: rotate(-16deg) scale(0.7);
}
/*------------Afterdance-------------*/
#afterdance .song-info {
	left: -10%;
	top: 82%;
	display: block!important;
	transform: none!important
}
#afterdance .song-info::before {
	height: 500%
}
#afterdance .song-info {
	margin-left: -4.5%
}
.title-container {
	left: 5%
}
#afterdance {
	background-image: url(https://i.imgur.com/4KJfOIn.png)!important;
	background-size: cover
}
/*-------------------------Room Info-----------------------*/
/* --------Dance Room Number-------- */
.state-dance #room-info{
  display: none;
}
.state-afterdance #room-info
{
  display: none;
}
.state-afterdance #players
{
  display: none;
}
.state-coachselection #room-info{
  display: none;
}
#room-info{
  display: none;
}
/*---------phone off----------*/
.state-songselection .connect-phone-info {
    top: 2% !important;
    left: -23.5% !important;
}
/*-----------Hi-Score------------*/
#preview .social-stats.stat-song-rewards,
.social-stats.stat-total-stars, .score__number::before
{
  display: none!important;
}
.score__display::after{
	border-left:2px solid #fff;
	height:90%;
	top:10%;
}
.highscore-display
{
  top: 35%;
  left:20%;
}
.score__number {
   left: 3%;
    margin-top: -0.42em;
    margin-bottom: 0;
    top: 41.5%;
    text-shadow: none !important;
    font-size: 85%;
    text-align: left;
    color: #ffffff;
}
.score__stars {
    opacity: 1;
     top: 48%;
    left: 7%;
}
.score__owner .owner-name{
  color:#ffffff;
  position: absolute !important;
  font-size:80%;
  top:-0.2rem;
  left: -4%;
}
.pre-beat.odd .song-preview:not(.transitioning) {
    animation: BrightnessStarBeat 1s infinite !important;
}
/*------*/
.state-coachselection #preview .score__owner .owner-name {
    opacity: 0;
}
.state-coachselection #preview .score__number {
    opacity: 0;
}
.state-coachselection #preview .score__stars {
    opacity: 0;
}
.state-coachselection #preview .score__display::after {
    opacity: 0;
}
.state-coachselection .song-cover--hi-res::before {
    display: none;
}
.state-coachselection #preview .song-action--start .song-action__label {
    opacity: 0;
}
.state-coachselection #preview .song-action--start {
    opacity: 0;
}
.state-coachselection .song-cover--hi-res {
    opacity: 0;
}
.state-coachselection #preview .song-details--empty {
    opacity: 0;
}
.state-coachselection #preview .song-preview {
    opacity: 0!important;
}
.coach-selection__list
  {
    top: 14%;
    right: 14%;
    width: 145%;
    height: 130%;
  }
.coach-selection__title {
    text-align: right;
     right: 29.5%;
    top: 20%;
    position: fixed;
}
/*-------------------------------Players-------------------------------*/
#players
  {
    position: absolute;
    top: 0px;
    left: 17.5%;
    z-index: 990;
    width: 67%;
    height: 20%;
  }
.state-dance #players {
    left: 16.5%;
}
.state-dance #players .player-stars {
    bottom: 29%;
}
.dancercard.dancercard .player-name {
    text-shadow: 2px 0px 1px rgba(150, 150, 150, 1) !important;
    bottom: 17%;
    font-size: 86%;
 -webkit-mask-image: -webkit-gradient(linear, left bottom, right bottom, color-stop(0.00, rgba(0, 0, 0, 0)), color-stop(0.35, rgba(0, 0, 0, 0.95)), color-stop(0.50, rgb(255, 255, 255)), color-stop(0.65, rgba(0, 0, 0, 0.9)), color-stop(1.00, rgba(0, 0, 0, 0)));
}
.dancercard .player-name em {
    text-shadow: 2px 0px 1px rgb(47, 47, 47);
    overflow: visible;
}
.dancercard .player-color,
.player--new .player-color {
    transform: scale(1.4, 0.2);
    top: 32%;
}
.state-dance #players .crown {
    position: absolute;
    top: 41.5%;
    left: -12.2%;
    transform: rotate(-15deg);
}
html:not(.ipad) .hud.beat>#players.animate .player-color {
    animation: none;
}
.player-color::after {
    transform: rotate(-2deg);
    width: 97%;
}
.img-crown {
    display: none !important;
}
.dancercard .avatar-wrapper {
  top:-110%;
    left:-10%;
    transform: scale(0.7)
}
#players .controller-coins {
    top:54%;
}
/*-----------Display-------------*/
 .song-details--empty
  {
    animation: none !important;
    left: -53%;
    top: 2%;
  }
#preview .song-preview {
	left: 175%!important;
	top: 0%!important;
	transform: scale(1.3)!important
}
.song-action {
	left: 143%;
	top: 65%;
	transform: scale(1.0)
}
  .video-preview
 {
    right: -54%;
    transition: transform 0.3s;
  }
 .sprite--stars, .sprite-description,
.sprite-description,  .sprite__svg-container svg
  {
    top: 10000%;
  }
  .song-action__price-tag
  {
    right: -10000000%;
  }
.preview--bg::before
  {
    position: absolute;
    top: -0.41rem;
    right: 10.7rem;
}
/*-------------------------------Botões------------------------------*/
.exit-btn--play-again {
    left: 0rem;
    color: #f2792a;
    top: 88%;
    text-align: left;
}
.exit-btn--play-again::after {
    border-left: 2px solid !important;
    left: 104% !important;
    height: 69% !important;
}
.exit-btn--play-again {
    left: 1.3rem;
    color: white;
    top: -24%;
    text-align: left;
}
.exit-btn--play-again::before {
    top: 24.5% !important;
    left: 151% !important;
    background-image: url(https://dl.dropbox.com/s/q89r6tixxi9rjuo/icon_playAgain.png?dl=0);
    width: 22%;
}
.exit-btn::after {
    content: '';
    position: absolute;
    left: 102%;
    top: -41%;
    height: 1.37rem;
    border-left: 2.2px solid white;
    transform: rotate(-11deg);
}
.exit-btn--continue {
    right: 14.8%;
    top: 89.9%;
    font-weight: bolder;
    background: url(https://dl.dropbox.com/s/bgiwva4kv1hqmlm/button_play.png?dl=0) left/ 15% no-repeat;
}
.exit-btn::before {
    top: -100%;
    font-weight: bolder;
    left: 99%;
    background-size: 93%;
}
.state-afterdance #players .player .controller-coins span::before {
    background: url(https://dl.dropbox.com/s/j9vc05vx8yaqq7v/Overlay_MojoCoin.png?dl=0) center/ 1em no-repeat;
    transform: scale(1.6);
    border-radius: 12em;
    border: 1.5px solid;
    width: 0.9em;
    margin: 5% -15%;
    height: 0.9em;
}
.state-afterdance #players .player .controller-coins span {
    left: 29%;
}
.state-afterdance #players .player .controller-coins::after {
    position: fixed;
    height: 3em;
    z-index: -1;
    margin: -61% 0 0 65%;
    transform: rotate(-11deg);
    background: white;
    width: 1.5%;
    content: "";
    display: block;
}
.state-afterdance #players .player .controller-coins {
    position: fixed;
    left: 450%;
    bottom: -83%;
    width: 100%;
}}
