	/*Changed text highlight*/
	::selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}
	::-moz-selection
	{
		background: var(--main-color) !important;
		color: var(--main-text) !important;
	}

	/*FUCK IT
	*, *:before, *:after
	{
		background-color: transparent !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		box-shadow: none !important;
	}*/

	/*Links*/
 	a
 	{
		outline-color: var(--main-color) !important;
	}
	a, a:visited
	{
		color: var(--main-color) !important;
		opacity: .9;
	}
	a:hover
	{
		opacity: 1;
	}


	/*Removing text decorations*/
	*
	{
		text-decoration: none !important;
	}

	body, html
	{
		background: var(--main-background) !important;
		color: var(--dimer-text) !important;
	}

	/*Main page*/
	.search_bar .search_bar__form__input
	{
		background: var(--main-background) !important;
		color: var(--dimer-text) !important;
	}
	.search_bar .search_bar__form__reset span, .search_bar .search_bar__form__search span
	{
		color: var(--dimer-text) !important;
	}
	.search_bar .search_bar__form__reset span:hover, .search_bar .search_bar__form__search span:hover
	{
		color: var(--main-color) !important;
	}
	.home__logo__container.home__logo__container--default .home__logo
	{
		filter: brightness(400%) grayscale(100%);
	}
	.suggester, .suggester *
	{
		background-color: var(--hover-background) !important;
		color: var(--main-text) !important;
		border-color: var(--hover-background)
	}

	.header__add-qwant
	{
		background: var(--hover-background) !important;
	}
	.header__add-qwant:hover
	{
		background: var(--main-color) !important;
	}

	.header__text__learn-more__icon
	{
		display: none !important;
	}


	.footer
	{
		background-color: var(--hover-background) !important;
	}
	.footer__block--left .footer__baseline
	{
		color: var(--dimer-text) !important;
	}


	/*Settings*/
	.form_page, .form_page *, .settings_url .settings_url_textbox
	{
		background-color: var(--main-background) !important;
		color: var(--main-text) !important;
		border-color: var(--hover-background)
	}
	.form_page .button
	{
		background: var(--hover-background) !important;
	}
	.form_page .button:hover
	{
		background: var(--main-color) !important;
	}
	.icon-success::before
	{
		display: none;
	}

	.toggle-switch input:checked + label::before
	{
		background: var(--main-color) !important;
	}
	.toggle-switch input + label::after
	{
		background-color: var(--main-text) !important;
	}
	.toggle-switch input + label::before
	{
		background-color: var(--hover-background) !important;
	}
	.select-placeholder:hover
	{
		color: var(--main-color) !important;
	}


	/*Sidebar*/
	.sidebar
	{
		background-color: var(--second-background) !important;
	}
	.sidebar__item__label, .icon-qwant::before
	{
		color: var(--main-text) !important;
	}


	.wrapper-dropdown .dropdown, .wrapper-dropdown .dropdown .dropdown__item
	{
		background-color: var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
	}
	.wrapper-dropdown .dropdown .dropdown__item
	{
		color: var(--main-text) !important;
	}
	.wrapper-dropdown .dropdown .dropdown__item:hover, .result_filter__item--dropdown .wrapper-dropdown .select-placeholder:hover, .wrapper-dropdown.active .select-placeholder
	{
		color: var(--main-color) !important;
	}


	.result h3 a
	{
		color: var(--main-text) !important;
	}
	.result:hover h3 a, .result:hover h3 a *
	{
		color: var(--main-color) !important;
	}
	p
	{
		color: var(--dimer-text) !important;
	}
	.result_actions__button::before, .result_actions__button::after
	{
		background-color: var(--main-text) !important;
	}
	.result_actions__circle
	{
		stroke: var(--main-color) !important;
	}
	.result_fragment:empty
	{
		filter: invert(100%);
	}
	/*Images*/
	.results-page *
	{
		background-color: transparent !important;
		border-color: var(--hover-background) !important;
		color: var(--main-text) !important;
	}
	.media_overlay
	{
		background: rgba(0,0,0,0.93) !important;
	}
	.results-column--images .result:hover .result__info, .results-column--images .result.result--zoomed .result__info
	{
		background: var(--hover-background) !important;
	}


	/*login page*/
	.input__text:active, .input__text:focus, .input__textarea:active, .input__textarea:focus
	{
		border-color: var(--main-color) !important;
	}
    .password-strength-indicator__jauge--weak
    {
    	background: var(--dimer-text) !important; 
	}
	.password-strength-indicator__jauge--medium
	{
		background: #FFB900 !important;
	}
	.password-strength-indicator__jauge--strong {
		background: #0FBD2B;
	}
	.alert__item
	{
		background-color: var(--hover-background) !important;
		border-color: var(--hover-background) !important;
	}
	.alert__item__close
	{
		color: var(--main-text) !important
	}
	.alert__item__close:hover
	{
		color: var(--main-color) !important;
	}
	.header__user-menu .wrapper-dropdown .select-placeholder
	{
		color: var(--main-text) !important;
	}

	/*Mobile main page*/
	.header, #main, .result-list__top,
	.results-page--all-mobile,
	.results-page--web,
	.results-page--news,
	.results-page--social,
	.results-page--images,
	.results-page--videos,
	.results-page--shopping,
	.results-page--music
	{
		background: var(--main-background) !important;
		color: var(--dimer-text) !important;
	}
	.result-list__carousel-item--current .result-list__carousel__title,
	.result-list__carousel__title
	{
		color: var(--main-text);
	}
	.home__logo__container .header__mobile-logo--large
	{
		filter: brightness(400%) grayscale(100%);
	}
	.header__mobile-icon
	{
		filter: brightness(200%) grayscale(100%);
	}

	/*Mobile sidebar*/
	.sidebar__mobile, .sidebar__mobile__links
	{
		background: var(--main-background);
		border-color: var(--hover-background);
	}
	.sidebar__mobile__settings, .sidebar__mobile__user
	{
		border-color: var(--hover-background);
	}
	.sidebar__mobile__settings h3, .sidebar__mobile__links h3
	{
		color: var(--main-text);
	}
}

@-moz-document domain("about.qwant.com") {

	.site-header, .header_inner
	{
		background: var(--second-background) !important;
		border-color: var(--hover-background) !important;
	}
	.qwant-logo
	{
		filter: grayscale(100%) brightness(500%);
	}

	.main-navigation .menu-item::after
	{
		background: var(--main-color) !important;
	}

	.wpml-ls-legacy-dropdown a.wpml-ls-item-toggle::after, .wpml-ls ul
	{
		color: var(--main-text) !important;
		border-color: var(--hover-background) !important;
	}
	.wpml-ls ul:hover, .wpml-ls ul:hover *
	{
		background-color: var(--hover-background) !important;
		box-shadow: var(--shadow) !important;
		color: var(--main-text) !important;
	}
	.wpml-ls-legacy-dropdown .wpml-ls-item span:hover
	{
		color: var(--main-color) !important;
	}

	.home_button_see_all a
	{
		border-color: var(--hover-background) !important;
	}

	.separator1, .separator2
	{
		filter: invert(90%);
	}
	hr
	{
		border-color: var(--hover-background) !important;
	}
	footer
	{
		background-color: var(--second-background) !important;
	}
}
