style.css:
@-moz-document url-prefix("https://wix.github.io/") {
body, .content {
    background-color: #121212;
    border-color: #121212;
}

.sidebar, .markdown-section pre, .markdown-section pre > code, .markdown-section p.tip, .markdown-section code, body.sticky .sidebar, body.sticky .sidebar-toggle {
    background-color: #212121;
    border-color: #212121;
}

.search input::-webkit-search-decoration, .search input::-webkit-search-cancel-button, .search input {
    background-color: #323232;
}

.markdown-section pre > code, .markdown-section p.tip, .markdown-section p, .markdown-section ul, .markdown-section ol, .sidebar ul li a, .markdown-section > *, .search p {
    color: rgba(255,255,255,.7)
}

.anchor span, .sidebar li > p, .sidebar > h1 a, .markdown-section h1, .markdown-section h2, .markdown-section h3, .markdown-section h4, .markdown-section strong, .search input::-webkit-search-decoration, .search input::-webkit-search-cancel-button, .search input, .search h2, .app-nav a {
    color: #FFF;
}
}
