style.css:
@-moz-document domain(4chan.org), domain(4channel.org){
  body {
    background-image: url("https://i.redd.it/1iwcit1gidyy.jpg");
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-size: cover;
    color: #d6d6d6!important;
}
a {
    color: #96b0ff!important;
}
hr {
    display:none;
}
#delform hr {
    display:block;
    border-top:1px solid #004ebd!important;
}
#blotter {
    width: 100% !important;
}
.boxbar {
    background-color: #7a0088!important;
    color: #fff;
}
#announce {
    background-color: #4e0244!important;
    color: #d6d6d6!important;
}
#delform {
    background-color: #380030!important;
}
.box-outer {
    border-style: none;
    background-color: #380030!important;
    color: #d6d6d6!important;
}
#boards a {
    color: #0361f3;
}
.c-thread img {
    border-style: none;
}
ul, li {
    border-style: none!important;
}
#ft li {
    background: #380030!important;
}
#bannerCnt {
    background-color: #380030!important;
    border-style: none;
    width: auto!important;
    padding-top: 10px;
    padding-bottom: 10px;
}
.boardTitle {
    background-color: #380030!important;
    width: auto!important;
}
.globalMessage {
    background-color: #380030!important;
    width: auto!important;
    padding-top: 10px;
    padding-bottom: 10px;
}
.desktop {
    background-color: #380030!important;
    width: auto!important;
}
.reply {
    background-color: #6f005f!important;
    width: auto!important;
    border-style: none!important;
    margin-bottom: 10px;
}
.thread {
    background-color: #380030!important;
    width: auto!important;
    height: 100%;
    margin: 10px;
}
}
