style.css:
@-moz-document domain("skuidsite.com"), domain("force.com")
{
  .nx-pagebuilder-component.nx-pagebuilder-component-readonly .nx-pagebuilder-header-component .nx-pagebuilder-component-body,
.nx-pagebuilder-component.nx-pagebuilder-component-readonly .nx-pagebuilder-footer-component .nx-pagebuilder-component-body
  {
    display: none;
  }

  .nx-pagebuilder-component.nx-pagebuilder-component-readonly .nx-pagebuilder-header-component .nx-page-builder-hiddenheader-title::after
  {
    content: " and footer are hidden by your Stylish Skuid extension";
  }

  .nx-pagebuilder-component.nx-pagebuilder-component-readonly .nx-pagebuilder-header-component .nx-page-builder-hiddenheader-title
  {
    color: #FFF;
  }
}
