}
.help-button .fa::before {
    color: #565656;
}
.help-button:hover .fa::before {
    color: #cacaca;
}
.sidebar-link,
.main-left-navbar li a {
    color: #0097A7;
}
.broadcast-nav {
    right: 0;
}
.broadcasts-right-sidebar {
    min-width: 300px;
    min-height: 100vh;
    max-height: calc(100% - 80px - 44px) !important;
    height: auto !important;
    background-color: #282828;
}
.broadcasts-filters-btn {
    color: #CACACA;
    background-color: #282828;
    font-size: 18px;
    text-align: left;
    margin: 0;
    min-height: 44px;
    height: 100%;
    width: 100%;
    border: 0 solid;
}
#sidebarCollapse {
    border: 0 solid transparent;
    border-radius: 23px 0px 0px 23px;
    background-color: #282828;
    margin-right: 0;
    z-index: 666;
    position: relative;
}
#sidebarCollapse::hover {
    border: 0 solid transparent;
    border-radius: 23px 0px 0px 23px;
    color: #1d2120;
    z-index: 666;
    position: relative;
}
.open > .dropdown-menu {
    border: 0;
}
#broadcasts-container {
    background: #282828;
    min-height: 100vh;
}
.content-style {
    color: #fff;
}
.broad-content {
    background-color: rgba(200, 154, 154, 0);
    padding: 20px;
    margin: 0;
    width: 100%;
}
.btn:hover,
.broadcasts-filters-btn:hover,
.btn:focus,
.broadcasts-filters-btn:focus,
.btn.focus,
.focus.broadcasts-filters-btn {
    color: #eee;
    background: #1d2120;
    text-decoration: none;
}
@media screen and (max-width: 480px) {
    .page-content {
        margin: 0 !important;
    }
    .profile-banner {
        background-size: cover;
    }
}
@media screen and (min-width: 480px) and (max-width: 768px) {
    .page-content {
        margin: 60px 0 0 0 !important;
    }
}
}
