style.css:
@-moz-document domain("poedb.tw") {
/* main */

* {
	color: #CAC2A8;
}
body, .page-content {
	background-color: #060606;
}

/* box element */

.panel-heading {
    background-color: #141414 !important;
    background-image: none !important;
    color: #CAC2A8 !important
}

.panel-body {
    background-color: #0c0b0b !important;
    background-image: none !important;
    color: #CAC2A8 !important
}

.navbar-default div {
	background-color: #0c0b0b;
}
.navbar-default {
	border-color: #141414;
}
.panel, .well {
	background-color: #141414;
	background-image: none;
	border-color: #222;
}
.tags span {
	background-color: #0c0b0b;
	border-color: #222;
	line-height: 36px;
    color: #CAC2A8;
}
/* line */

hr {
	border-color: #141414;
}
li.double_th {
	border-bottom-color: #222;
}
/* form */

.form-control, textarea, input {
	background-color: #141414;
	border-color: #222;
	color: #CCC;
}
/* table */

table, tbody, thead, tr, th, td {
	border-color: #222 !important;
}
.table-striped>tbody>tr:nth-of-type(odd) {
	background-color: #0c0b0b;
}

table.dataTable tbody tr,
table.dataTable.display tbody tr.even>.sorting_1, table.dataTable.order-column.stripe tbody tr.even>.sorting_1,
table.dataTable.display tbody tr.even>.sorting_2, table.dataTable.order-column.stripe tbody tr.even>.sorting_2,
table.dataTable.display tbody tr.even>.sorting_3, table.dataTable.order-column.stripe tbody tr.even>.sorting_3,
table.dataTable.display tbody tr.even>.sorting_4, table.dataTable.order-column.stripe tbody tr.even>.sorting_4,
table.dataTable.display tbody tr.odd>.sorting_1, table.dataTable.order-column.stripe tbody tr.odd>.sorting_1,
table.dataTable.display tbody tr.odd>.sorting_2, table.dataTable.order-column.stripe tbody tr.odd>.sorting_2,
table.dataTable.display tbody tr.odd>.sorting_3, table.dataTable.order-column.stripe tbody tr.odd>.sorting_3,
table.dataTable.display tbody tr.odd>.sorting_4, table.dataTable.order-column.stripe tbody tr.odd>.sorting_4 {
    background-color: transparent;
}
table.dataTable.stripe tbody tr.odd, table.dataTable.display tbody tr.odd {
	background-color: #0c0b0b;
}
table.dataTable.hover tbody tr:hover, table.dataTable.display tbody tr:hover {
    background-color: #141414;
}

/* gem */

.gem_blue, A.gem_blue {
	color: #337ab7;
}
.gem_red, A.gem_red {
	color: #ff7070;
}
.gem_green, A.gem_green {
	color: #b8ffb8;
}
/* link */

a {
	color: #DFCF99;
}
a:focus, a:hover {
	color: #FFF;
}
}
