style.css:
@-moz-document url(http://orders.gudfood.com.ua/order) {
  .item {
    width: 100%;
  }
  .thumbnail {
    min-height: inherit;
  }
  .thumbnail a > img,
  .thumbnail > img {
    display: none;
  }
  .thumbnail .caption::after {
    content: "";
    display: table;
    clear: both;
  }
  .thumbnail .caption h4 {
    float: left;
  }
  .thumbnail .caption p {
    float: left;
    clear: both;
  }
  .thumbnail .caption .row {
    float: right;
  }
  .thumbnail .caption .row div:nth-of-type(1) {
    display: none;
  }
}
