    padding: 0;
    line-height: 2.3em;
    background: none;
    border-bottom: none;
    border-right: none;
}
.b-breadcrumb-a {
    background: rgba(242, 247, 252, .85);
    border-radius: 5px 5px 0px 0px;
}
.b-pagination-a .b-pagination-list .b-pagination-item>a, .b-pagination-a .b-pagination-list .b-pagination-item>span {
    border-radius: 3px;
}
.button>a, .u-button>a .button>span, .u-button>span, a.button {
    color: #fff!important;
    font-weight: bold;
    transition: all .25s ease-in-out;
}
div.tabbed-content {
    background: #f2f7fc;
}
ul.tabbed-tabs>li.selected {
    background: #f2f7fc;
}
ul.tabbed-tabs>li {
    background: #ebf2fa;
}
ul.tabbed-tabs>li:hover {
    background: #f2f7fc;
}
ul.tabbed-tabs>li>a {
    color: #5b8ba4;
}
ul.tabbed-tabs>li>a:hover {
    color: #486f84;
}
ul.tabbed-tabs>li.selected>a {
    color: #5b8ba4;
}
ul#navigation .section ul li a:hover, ul#navigation .section ul li.selected a {
    background: #d7e4f4;
    color: #365363;
    text-shadow: none;
    border-bottom: 1px solid #8ea3aa;
}
ul#navigation .section ul li a {
    background: #f2f7fc;
    color: #486f84;
    text-shadow: none;
    border-bottom: 1px solid #8ea3aa;
}
#page-container-left {
    border-right: none;
    background: none;
}

ul#navigation li a, ul#navigation li.section>span {
    text-shadow: none;
}

.p-comments .p-comment-actions .user-actions>li a:hover {
    color: #fff;
}

.p-comments-a .p-comment-user-background {
    background: #f2f7fc;
}

.p-comments-a .p-comment-footer {
    background: #f2f7fc;
}

.sub-caption span {
    background: none;
}

.sub-caption {
    background: none;
}

.b-tab-b .b-tab-item.selected a {
    background: #f2f7fc;
    border-radius: 3px 3px 0px 0px;
}
.b-tab-b .b-tab-item>a:hover, .b-tab-b .b-tab-item a:active:hover {
    background: #f2f7fc;
    border-radius: 3px 3px 0px 0px;
}
.b-tab-b .b-tab-item>a, .b-tab-b .b-tab-item a:active {
    background: #ebf2fa;
    border-radius: 3px 3px 0px 0px;
}
.b-tab-b .b-tab-nav {
    border-bottom: none;
}

/*Mod usernote text color*/
.user-role-moderator .forum-post-body-content {
    color: inherit;
}

h5.form-notes.help {
	background-color: #ebf2fa;
    border-color: #8ea3aa;
}
