style.css:
@-moz-document domain("gitlab.com"), regexp(".*gitlab.*\\.com.*") {
.container-limited {
    max-width: 95%;
}
.table.ci-table .branch-commit .ref-name {
    max-width: 80%;
}
.table.ci-table .branch-commit .commit-title {
    max-width: 80%;
    min-width: 150px;
}
.table.ci-table .branch-commit .ref-name {
    max-width: 90%;
}
.ci-table .branch-commit .ref-name {
    max-width: 90%;
    min-width: 150px;
}
.table-section.section-20 {
    min-width: 35%;
    max-width: 60%;
}
.pipelines .ci-table .stage-cell,
.pipelines .ci-table .pipeline-actions,
.pipelines .ci-table .pipelines-time-ago {
    max-width: 15%;
    min-width: 5%;
}
.pipelines .ci-table .pipeline-tags,
.pipelines .ci-table .commit-link {
    max-width: 10%;
    min-width: 5%;
}
}
