style.css:
@-moz-document url-prefix("https://www.lordcraft.net/") {
body {
    background: #1c262f;
    color: #fff !important;
}
a {
    color: #00d2ff;
}
a:hover {
    color: #17b3ed
}
.ipsType_pageTitle {
    color: #bdbdbd
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    background: #8e54e9;
    background: -moz-linear-gradient(left, #8e54e9 0%, #4776e6 100%);
    background: -webkit-linear-gradient(left, #8e54e9 0%, #4776e6 100%);
    background: linear-gradient(to right, #8e54e9 0%, #4776e6 100%);
    color: #fff;
    font-size: 16px;
    border-bottom: transparent;
}
.ipsWidget .ipsWidget_vertical .ipsBox .ipsWidget_inner .ipsPad {
    color: red;
}
li.ipsWidget.ipsWidget_horizontal.ipsBox > h3.ipsType_reset.ipsWidget_title {
    background: #8e54e9;
    background: -moz-linear-gradient(left, #8e54e9 0%, #4776e6 100%);
    background: -webkit-linear-gradient(left, #8e54e9 0%, #4776e6 100%);
    background: linear-gradient(to right, #8e54e9 0%, #4776e6 100%);
    color: #fff;
    font-size: 16px;
    border-bottom: transparent;
}
.ipsBox {
    border: 1px solid #364350;
    box-shadow: none;
}
.ipsBox:not( .ipsBox_transparent):not( .ipsModerated) {
    background: #3c4854;
}
.ipsType_richText {
    color: #fff;
    font-size: 14px;
}
.ipsType_sectionTitle {
    background: #8e54e9;
    background: -moz-linear-gradient(left, #8e54e9 0%, #4776e6 100%);
    background: -webkit-linear-gradient(left, #8e54e9 0%, #4776e6 100%);
    background: linear-gradient(to right, #8e54e9 0%, #4776e6 100%);
    font-size: 15px;
    text-transform: capitalize;
    border-bottom: transparent;
}
a.ipsType_sectionTitle,
.ipsType_sectionTitle a {
    color: #fff;
}
.ipsAreaBackground_reset {
    background: #3c4854;
}
.ipsDataList.ipsDataList_zebra .ipsDataItem:not( .ipsDataItem_selected):not( .ipsModerated):not( .ipsDataItem_new):not( .ipsDataItem_success):not( .ipsDataItem_warning):not( .ipsDataItem_error):not( .ipsDataItem_info):not( .ipsDataItem_status):nth-child(even) {
    background: #3c4854;
}
.ipsItemStatus:not( .ipsItemStatus_large) {
    color: #47ffbc;
}
.ipsType_blendLinks {
    color: #a4aeb0;
}
.stats-post {
    background: #1c262f;
    border: none;
}
.stats-post2 {
    background: #1c262f;
    border: none;
    vertical-align: -1px;
}
.userBlock {
    border: none;
}
.userBlock_info {
    background: #3c4854;
}
.userBlock_counters a {
    color: #fff;
    border-top: 1px solid #637486;
}
.userBlock_counters a:first-child {
    border-top: 1px solid #637486;
    border-right: 1px solid #637486;
}
.ipsDataItem {
    border-color: #637486;
}
#ipsLayout_footer a,
#ipsLayout_footer p {
    color: #fff;
}
.ipsBreadcrumb [data-action="defaultStream"],
.ipsBreadcrumb [data-action="markSiteRead"] {
    color: #fff;
}
.ipsApp .ipsButton_link.ipsFollow,
.ipsApp .ipsButton_link.ipsPromote {
    background: #313f4c;
    border: none;
}
.ipsFollow .ipsCommentCount,
.ipsFollow .ipsCommentCount:after {
    background: #1c262f;
    border: none;
    color: #fff
}
.ipsWidget .ipsTabs_small {
    background: #3c4854;
}
.ipsTabs_activeItem {
    background: #fff;
    color: #5b6c7c;
}
.ipsWidget .ipsTabs_small .ipsTabs_item:not( .ipsTabs_activeItem) {
    color: #fff;
}
.ipsTabs_panel {
    background: #3c4854;
}
.ipsRepBadge_positive,
.ipsRepBadge_positive:hover {
    color: #a8d77e !important;
}
.ipsAreaBackground_light {
    background: #1c262f;
}
.ipsType_minorHeading {
    color: #fff;
}
.ipsButtonBar {
    background: #2b3844;
    box-shadow: none;
}
.ipsButtonBar ~ .ipsButtonBar {
    border: none
}
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_page a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_next a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_prev a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_first a,
.ipsApp .ipsButtonBar .ipsPagination .ipsPagination_last a {
    background: #1c262f;
    color: #fff
}
.ipsButtonBar .ipsPagination .ipsPagination_pageJump a {
    color: #fff;
}
.ipsMenu {
    background: #1c262f;
    border: none;
}
.ipsApp .ipsField_autocomplete,
.ipsApp textarea,
.ipsApp input[type="text"],
.ipsApp input[type="password"],
.ipsApp input[type="datetime"],
.ipsApp input[type="datetime-local"],
.ipsApp input[type="date"],
.ipsApp input[type="month"],
.ipsApp input[type="time"],
.ipsApp input[type="week"],
.ipsApp input[type="number"],
.ipsApp input[type="email"],
.ipsApp input[type="url"],
.ipsApp input[type="search"],
.ipsApp input[type="tel"] {
    background: #3c4854;
    color: #fff
}
.ipsApp .ipsButton_primary {
    background: #445769;
}
.ipsType_light {
    color: #fff;
}
.ipsBreadcrumb > ul > li {
    color: #fff;
}
.ipsButtonBar .ipsButtonRow li > a,
.ipsButtonBar .ipsButtonRow li > button,
.ipsButtonBar .ipsButtonRow li > span {
    background: #1c262f;
    color: #fff
}
.ipsMenu_title {
    background: #344350;
}
.ipsMenu_sep {
    border-top: 1px solid #445666;
}
.ipsMenu_item > a,
.ipsMenu_item > span {
    color: #fff;
}
.ipsMenu_item > a:hover {
    background: #445769 !important;
}
.ipsComment_highlighted.ipsBox:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsComment_selected):not(.ipsComment_popular),
.ipsComment_highlighted:not(.ipsBox_transparent):not(.ipsModerated):not(.ipsComment_selected):not(.ipsComment_popular) .ipsAreaBackground_reset {
    background: #3c4854;
    border: none;
    box-shadow: none;
}
.cAuthorPane_author {
    background: #26323e;
    border: none;
}
.cAuthorPane {
    border-right: 1px solid #4a5a6b;
}
.uip {
    border: none;
}
.leftinfo {
    color: #fff;
}
.rightinfo {
    color: #fff;
}
.ipsReact_reactCount {
    background: #1c262f;
}
.ipsReact_reactCount > span > span:last-child,
.ipsReact_reactCount > a > span:last-child {
    color: #fff
}
.ipsReact_button {
    background: #1c262f;
}
.ipsPager_type {
    color: #fff;
}
.ipsPager .ipsPager_next a:after,
.ipsPager .ipsPager_prev a:after {
    color: #fff;
}
.ipsAreaBackground {
    background: #3c4854;
    border: none;
}
.ipsComposeArea_unavailable .ipsComposeArea_editor {
    background: #556575;
}
.ipsComposeArea_dummy {
    background: #1c262f;
}
.ipsType_sectionTitle {
    color: #fff;
}
.ipsQuote {
    border-color: #445666;
    background: #1c262f;
}
.ipsQuote_citation {
    background: #445666;
    color: #fff;
}
.ipsQuote .ipsQuote_citation a {
    color: #00d2ff;
}
.ipsApp .ipsField_autocomplete,
.ipsApp textarea,
.ipsApp input[type="text"],
.ipsApp input[type="password"],
.ipsApp input[type="datetime"],
.ipsApp input[type="datetime-local"],
.ipsApp input[type="date"],
.ipsApp input[type="month"],
.ipsApp input[type="time"],
.ipsApp input[type="week"],
.ipsApp input[type="number"],
.ipsApp input[type="email"],
.ipsApp input[type="url"],
.ipsApp input[type="search"],
.ipsApp input[type="tel"] {
    background: #1c262f;
}
.cke_wysiwyg_frame,
.cke_wysiwyg_div {
    background: #1c262f;
}
.ipsApp .ipsButton_link {
    background: #1c262f;
    border: none;
}
.ipsDialog > div {
    background: #2d3c4a;
}
.ipsDialog_title {
    color: #fff;
}
.ipsApp .ipsField_autocomplete,
.ipsApp textarea,
.ipsApp input[type="text"],
.ipsApp input[type="password"],
.ipsApp input[type="datetime"],
.ipsApp input[type="datetime-local"],
.ipsApp input[type="date"],
.ipsApp input[type="month"],
.ipsApp input[type="time"],
.ipsApp input[type="week"],
.ipsApp input[type="number"],
.ipsApp input[type="email"],
.ipsApp input[type="url"],
.ipsApp input[type="search"],
.ipsApp input[type="tel"] {
    border-color: #2d3c4a;
}
.ipsDialog_close {
    color: #fff;
}
.cFilePrice,
.cWidgetPrice {
    color: #f06c6c;
}
.ipsType_sectionHead {
    color: #fff;
}
.ipsStreamItem_snippet .ipsType_richText {
    color: #fff;
}
.cProfileSidebarBlock.ipsLeaderboard_trophy_1 {
    background: #f99a45;
}
.cProfileSidebarBlock.ipsLeaderboard_trophy_1:hover {
    color: #704926;
}
.ipsFieldRow_section {
    background: #1c262f;
    color: #fff;
}
.nes {
    background: #334452;
    border: none;
    color: #fff;
}
html[dir] .ipsButton_split > .ipsButton.ipsButton_link,
html[dir] .ipsButton_split > li > .ipsButton.ipsButton_link {
    border: none;
}
.ipsTabs {
    background: #455d72;
}
.ipsApp .ipsButton_alternate {
    background: #1c262f;
}
.ipsAutocompleteMenu {
    background: #1c262f;
}
.ipsAutocompleteMenu li:hover {
    background: #334452;
}
}
