style.css:
@-moz-document url-prefix("https://www.brickplanet.com/") {

.container-header {
    background: #cc4b37;
    color: white;
    border-radius: 5px 5px 0 0;
    font-size: 16px;
}

strong {
    font-weight: 700;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/forum/thread/") {
.forum-thread-header {
    background: #cc4b37;
    padding: 6px 0;
    font-size: 12px;
    color: #FFFFFF;
    line-height: 1.5;
    margin-top: 15px;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/") {
.side-bar .right {
    float: right;
    background: #cc4b37;
    color: #FFFFFF;
    font-size: 11px;
    font-weight: 600;
    padding: 6px;
    border-radius: 4px;
    text-align: center;
    line-height: 1;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/forum/") {
a {
    color: #ff2200;
}
}

@-moz-document url-prefix("https://www.brickplanet.com/forum/") {
.button-green {
    background: #ff2200 !important;
    color: #FFFFFF !important; 
}
}

@-moz-document url-prefix("https://www.brickplanet.com/forum/thread/") {
.button-green {
    background: #cc4b37 !important;
    color: #FFFFFF !important;
}


}
