style.css:
@-moz-document url("https://app.wire.com/") {
div#conversation.conversation .text{
    color: #ddd;    
}
div#conversation.conversation{
    background-color: #333;    
}
code>span.hljs-keyword{
	color: #cc0;  
}
div#conversation.conversation pre, div#conversation.conversation code{
	background-color: #000;    
}
#conversation-input-bar, #conversation-title-bar{
    color: #ddd;
	background-color: #111; 
}
#giphy-modal .modal-content {
    color: #ddd;
	background-color: #111;
    border-style: solid;
    border-width: 1px;
    border-color: #888;
}
div.message>.message-body>div.message-body-actions, div.message-body-actions span.context-menu, div.message-body-actions:hover, div.message-body-actions time.time:hover, div.message-body-actions time.time{
    color: #ddd !important;
    background-color: #333 !important;
}
}
