style.css:
@-moz-document domain("tomshw.it") {
/* tema dark sperimentale, attenzione ho abusato di important */

body {
    overflow: inherit !important;
}

p,
ul,
ol,
h1,
h2,
h3,
h4,
h5,
h6,
table,
div {
    color: #dbdbdb !important;
}

.guide_acquisto_list a:link,
.guide_acquisto_list a:visited, 
.guide_acquisto_list a:focus, 
.guide_acquisto_list a:active {
    color: #909090;
}
.guide_acquisto_list a:hover {
  color: #d92328 !important ;
}


.posts_pagination .nav-links a:hover {
    color: #d92328 !important;
    text-decoration:none;
    background-color:#404040;
}
.posts_pagination .nav-links a:link, 
.posts_pagination .nav-links a:visited, 
.posts_pagination .nav-links a:focus, 
.posts_pagination .nav-links a:active {
    color: #dbdbdb;
}
.page-numbers {
    padding:9px !important;
}

.post-message {
    color: 
    #d2d2d2;
}
blockquote, .cta-container,
blockquote p, 
.cta-container p:first-child, 
blockquote ul {
	background-color: #505050 !important;
}

/*bg*/

.right_sidebar_fixed,
.right_sidebar,
.guide_acquisto_list div.row .col,
 div.app_download.right_sidebar_box div.row.justify-content-center.align-items-center {
    background-color: #101010 !important;
    color: fff;
}
.right_sidebar a {
    color: #939393 !important;
}

body.single .content_container .main_single_col,
.container.content_container .row,
.row_main_article,
.disqus_comments_loader,
body .container.content_container,
body.single.content_container,
.item_short_desc,
.tagstyle
.col_break_out {
    background-color: #303030 !important;
}

.item_short_desc > h3 a {
    color: #dbdbdb !important ;
}
.item_short_desc > h3 a:hover {
    color: #d92328 !important ;
}

body,
html {
    background-color: #000 !important;
}
h1.tagstyle {
	background-color: #101010;
}
.content_container div.row {
    background-color: #000 !important;
}
.right_sidebar .app_download {
    background-color: #101010;
}
body * {
    border-color: #2d2b2b !important;
}
.product_slider_item {
    background-color:#161616;
}
.generic_main_article table tr th {
	background-color: #3d3d3d;
}
/*struttura*/
.left_column {
    max-width: 70%;
}
.right_sidebar_fixed {
    max-width: 30%;
}
.edinet_adv_container .mh, .edinet_adv_container .bx, .edinet_adv_container .mob_bx, .edinet_adv_container .mob_slider {
	display:none
}


/*anti ADV message */
body > div:nth-last-of-type(1),
body > div:nth-last-of-type(2),
body > div:nth-last-of-type(3),
body > div:nth-last-of-type(4),
body > div:nth-last-of-type(5),
body > div:nth-last-of-type(6){
    display: none !important;
}
#super_container { /* fix */
    display: block !important;
}


}
