style.css:
@-moz-document domain("swiki.jp") {
/*メニューバー内*/
div#menubar {
    background-color: #151520;/*メニューバー背景色*/
    color: #ffffff;/*メニューバー文字色*/
}

#menubar a:link { 
    color: #00bfff; /*未訪問のリンク*/
}
#menubar a:visited {
    color: #cd5c5c; /*訪問済みのリンク*/
}
#menubar a:hover { 
    color: #ffffff;/*ポイント時のリンク*/
    background-color:#333333;/*ポイント時の背景*/
}

td.ltable {
    background-color: #000000;/*メニューバー周り背景色*/
    border-color: #000030;/*メニューバー周り境界色*/
}
/*メニューバー内ここまで*/

/*本文エリア*/
div#topicpath {
    color: #4080ff;/*サイト場所の色*/
    font-weight:700;/*サイト場所太字化*/
    font-size:130%;/*サイト場所大字化*/
}

div#contents_frame {
    background-color: #212121;/*ページ背景色*/
    color: #999999;/*ページ文字色*/
}

#contents_frame a:link { 
    color: #00bfff; /*未訪問のリンク*/
}
#contents_frame a:visited {
    color: #cd5c5c; /*訪問済みのリンク*/
}
#contents_frame a:hover { 
    color: #ffffff;/*ポイント時のリンク*/
    background-color:#333333;/*ポイント時の背景*/
}

div.contents{
    background-color: #333333;/*目次背景色*/
}

td.style_td {
    background-color: #1a1412;/*詳細図表色*/
    color: #bcaaf3;/*詳細図表文字色*/
}

td.rgn_buoon_cl{
    color: #ff4500;/*展開ボタン*/
    font-weight:500;/*展開ボタン太字化*/
}

/*本文エリアここまで*/
}
