style.css:
@-moz-document domain("htmlacademy.ru") {
.browser-container--ya .tab, .browser-container--o .tab, .service-layout-wrapper, .alert, .page-content, .no-certificates, .round-diagram__progress-inner::before, .tutors-story__inner, .ha-task-description-popup, .course-interface--light .course-goals__content pre, .course-interface--light .course-theory__content pre, .course-interface--light .course-tab:first-child, .course-interface--light .course-container, .course-interface--light .course-extra
{
    background: #222;
}
body
{
    background-color: #222;
}
.browser-container--ya .tab
{
    border: 1px solid #9999996e;
}
pre, .ha-quest-task .ha-task-goals, .browser-container--ya .ha-task-controls, .browser-container--o .ha-task-controls, .introduction-screen, .ha-task-controls--ya .ha-task-controls-container, .ha-task-controls--o .ha-task-controls-container, .statistics, .secondary-nav__sub::before, .secondary-nav__item-link, .subscription__inner, .subscription-contents::before, .page-content--off-canvas .page-content__right::before, .descriptions, .tutors, .process, .mockups, .contents__inner-col, .badge::before, .course-theory__inner, .course-interface--light .console__line-message, .course-interface--light .course-browser__menu, .course-interface--light .course-editor__label-item, .course-interface--light .course-goals, .course-interface--light .course-layout__sidebar, .course-interface--light .course-tabs, .course-interface--light .course-theory__banner, .course-task--completed
{
    background: #333;
}
.course-interface--light .course-browser__menu-item, .course-interface--light .course-editor-controls__item, .course-interface--light .course-editor__label, .course-interface--light .course-goals__button--expand, .course-interface--light .course-separator-control, .course-interface--light .course-tab
{
   color: #bababa;
}
.course-interface--light .course-browser__address::after 
{
    background-image: linear-gradient(to right,#333 0%,#222 100%);
}
.browser-container--ya, .browser-container--o, .ha-quest-challenge .ha-task-controls--ya, .ha-quest-challenge .ha-task-controls--o
{
    border-color: #333;
}
.keks-hint, .task-notification
{
    display:none;
}
span, p, h1, h2, h3, pre, .ha-quest-task .ha-task-goals, .close, li, .introduction-screen__no-more, body
{
    color: #bababa;
}
hr
{
    border-color: #444;
    background: #222;
}
.browser-container--ya .ha-task-goals, .browser-container--o .ha-task-goals {
    border-color: #62c462;
    border-left-color: transparent;
    box-shadow: 0 0 3px #62c462;
}
.ha-quest-task .ha-goals-position-toggle
{
    filter: invert(80%)
}
code {
    background-color: #222;
    border: 1px solid #9999995c;
}
}
