    border: 2px solid rgba(255, 255, 255, 0.3) !important;
  }

  input[type=checkbox]:checked, input[type=radio]:checked
  {
    background-color: #8E1106;
/* CUSTOM COLOR: HEADER */
    border: 2px solid rgba(255, 255, 255, 0.15) !important;
  }

  input[type=checkbox]:checked:before
  {
    content: '';
    display: inline-block;
    height: 16px;
    width: 16px;
    margin: 2px 0 0 1px;
    background: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAA0AAAANCAQAAADY4iz3AAAAn0lEQVQYGW3BsQoBARzH8d/dyQ1SDAZJma2SVeENvIBsusFgN3mO8wDyDLyDkpLdgCgZ8P9SN3DO56MkXDy9uUrqqEdRSVRtaxf6eIojwxJjR1txOIy5c2NCSt9waNkWWFBWhDwNshIl5jw40FSENANbE1JixJUHAa4iZJliPNnbGZjh64MKISfAbENNceQIbMWRIb5+4VKnS0H/4SjmBdqkaBeNLuT2AAAAAElFTkSuQmCC) no-repeat;
    background-size: 13px;
  }

  input[type=checkbox]:hover, input[type=radio]:hover
  {
    -webkit-filter: drop-shadow(0px 0px 2px #fff);
    filter: drop-shadow(0px 0px 2px #fff);
  }

  label.option
  {
    padding: 2px;
  }

  label.option input[type=checkbox]:checked:before
  {
    margin: 0 0 0 -1px;
  }

/*** scrollbar (chrome) ***/
  *::-webkit-scrollbar
  {
    width: 5px !important;
    height: 15px !important;
    background: rgba(255, 255, 255, 0.15) !important;
  }

  *::-webkit-scrollbar-button
  {
    display: none !important;
  }

  *::-webkit-scrollbar-thumb, *::-webkit-scrollbar-track
  {
    border: 0 !important;
    box-shadow: none !important;
  }

  *::-webkit-scrollbar-thumb
  {
    min-height: 28px !important;
    background: rgba(255, 255, 255, 0.15) !important;
  }

  *::-webkit-scrollbar-track
  {
    background: transparent !important;
  }

  *::-webkit-scrollbar-corner
  {
    background: #101010 !important;
/* CUSTOM COLOR: BACKGROUND */;
  }
}

