style.css:
@-moz-document domain("clubtone.net"),
    url-prefix("http://clubtone.do.am") {

    @font-face {
        font-family: 'FontAwesome';
        src: url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0');
        src: url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?#iefix&v=4.7.0') format('embedded-opentype'), url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2?v=4.7.0') format('woff2'), url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff?v=4.7.0') format('woff'), url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf?v=4.7.0') format('truetype'), url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.svg?v=4.7.0#fontawesomeregular') format('svg');
        font-weight: normal;
        font-style: normal
    }

    /* common style */
    html {
        min-height: 100%;
    }

    body {
        background: radial-gradient(#f5f5f5, #c3c5ca);
    }

    a {
        text-decoration: none;
        color: #1d72bd;
    }

    a:hover {
        text-decoration: none;
        color: #28384a;
    }

    h1 {
        margin-bottom: 20px;
    }


    /* layout */
    div.page {
        padding-top: 45px;
        min-height: 500px;
    }

    div.content {
        border-radius: 0;
        border-top: 1px solid #fff;
        box-shadow: 0 0 50px rgba(0, 0, 0, 0.2);
        display: flex;
    }

    div#lastnews > h3:first-child {
        min-height: 20px;
        margin-bottom: 20px;
    }

    div#lastnews > div.selector {
        text-align: center;
    }

    div#top-link {
        cursor: pointer;
        display: none;
        position: fixed;
        left: 0;
        bottom: 0;
        padding: 0 0 15px 5px;
        z-index: 10000;
    }

    div#top-link a {
        width: 100px;
        background: rgba(0, 0, 0, 0);
        color: rgba(0, 0, 0, 0);
    }

    div#top-link a:before {
        position: absolute;
        bottom: 10%;
        right: 50%;
        margin-right: -30px;
        display: block;
        border-bottom: 30px solid #7a7b82;
        border-left: 30px solid transparent;
        border-right: 30px solid transparent;
        content: '';
    }

    div#top-link a:after {
        position: absolute;
        bottom: 10%;
        right: 50%;
        margin-right: -30px;
        margin-bottom: -25px;
        display: block;
        color: #7a7b82;
        content: 'Назад';
        width: 60px;
        text-align: center;
        font-size: 13px;
    }

    div#top-link:hover {
        background: #354b5d33;
    }

    div#com-sort {
        margin-left: 660px;
    }
    /* top menu */
    div#menu {
        border-radius: 0;
        border: none;
        background: linear-gradient(to top, #83868c, #8c9098);
        border-bottom: 1px solid #666;
        position: fixed;
        top: 0;
        left: 50%;
        margin-left: -670px;
        width: 1340px;
        z-index: 100;
    }

    div#menu > ul.nav > li {
        background-image: none;
    }

    div#menu > ul.nav a:link,
    div#menu > ul.nav a:visited {
        color: #fff;
        font-weight: 300;
    }

    div#menu a#home {
        background: none;
    }

    div#menu li ul {
        width: 216px;
        position: absolute;
        z-index: -1 !important;
        background: linear-gradient(to top, #515358 96%, #3e3e40 100%);
        top: -500px;
        opacity: 0;
        border-bottom-left-radius: 0;
        border-bottom-right-radius: 0;
    }

    #menu li:hover ul {
        top: 44px;
        opacity: 1;
        transition: none;
    }

    div#menu ul {
        transition: top .2s ease-out 0.2s, opacity .2s ease-out 0.2s;
    }

    div#menu li div#menu ul li,
    div#menu ul li a {
        transition: none;
    }

    div#menu li ul li:last-child:hover {
        border-bottom-left-radius: 0px;
        border-bottom-right-radius: 0px;
    }
    /* hide banners */
    div#menu + div#info,
    div#cycler,
    div.advert,
    div[id^=advert],
    div#need_enable_adblock {
        display: none !important;
    }
    /* top 10 */
    #top10t span.this,
    #top10t span:hover {
        background: #737a80;
        color: #fff;
    }

    #top10 ol li {
        position: relative;
        display: list-item;
        font: normal 9pt/13pt Arial, sans-serif;
        height: 27pt;
        overflow: hidden;
        border-bottom: 1px dashed #dedede;
        vertical-align: middle;
        padding: 2px 0 0 45px;
        color: #57a1c7;
    }

    #top10 ol:hover li:before {
        background: #737a80;
        transition: none;
    }

    #top10 ol li:hover {
        background: #dedede;
        color: #202020;
        transition: none;
        border-radius: 0;
        position: relative
    }

    #top10 ol li:hover:before {
        background: #fff;
        color: #737a80;
        transition: none;
    }

    #top10 ol li:before {
        font: normal 13pt/13pt 'Cuprum', Arial, sans-serif;
        position: absolute;
        left: 5px;
        background: #737a80;
        font-weight: 900;
        border-radius: 1px;
        text-align: center;
        width: 28px;
        height: 22px;
        top: 4px;
        padding: 6px 0 0 0;
        color: #fff;
    }
    /* music list */
    ul.mlist li {
        padding-bottom: 2px;
    }
    /* music page */
    h3 + div#wrap input#search[type="text"],
    h3 + div#wrap input#search_submit[type="submit"] {
        top: -10px;
    }

    h1 + div#wrap input#search[type="text"],
    h1 + div#wrap input#search_submit[type="submit"] {
        top: -20px;
    }

    div#forbidden {
        background: #737a80;
    }

    a.catSortLink {
        font-size: 12px;
        padding: 5px 10px;
    }

    div#conter > div,
    div#allEntries > div {
        height: auto
    }

    div#conter > div li,
    div#allEntries > div li {
        height: auto;
        border: none;
    }

    div#conter > div > li > div,
    div#allEntries > div > li > div {
        margin: 0 50px 0 110px;
        min-height: 45px;
    }

    li.pplay {
        background: linear-gradient(to top, #dedede, #ffffff);
        color: #444;
        border-top-right-radius: 0;
        border-bottom-right-radius: 0;
        position: relative !important;
        padding: 10px 10px 10px 48px !important;
        margin: 0 0 0 -48px !important;
        box-shadow: 5px 5px 20px #00000066;
        border-bottom: 1px solid #b7b7b7 !important;
        width: calc(100%+48px);
    }

    li.pplay:before {
        display: block;
        content: '';
        width: 0;
        height: 0;
        border-top: 20px solid transparent;
        border-bottom: 20px solid transparent;
        border-right: 20px solid #888;
        position: absolute;
        bottom: -20px;
        left: 0px;
        z-index: -1;
    }

    li.pplay .tn a {
        color: #444 !important;
    }

    .playlist .pt-link {
        top: 50%;
        width: 50px;
        height: 50px;
        text-indent: -9999px;
        position: absolute;
        margin-left: 0px;
        margin-top: -25px;
        background-size: 100px;
        background-position: 0 -6px;
        opacity: .2;
        transition: opacity 100ms ease-out;
    }

    .pt-link.zi {
        display: inline-block;
        text-indent: 0px;
        position: relative;
        width: auto;
        height: auto;
        margin-left: 0;
    }

    .pt-link.pt-playing {
        background-position: -50px -6px;
    }

    div#conter span.tc img,
    div#allEntries span.tc img {
        width: 50px;
        height: 50px;
    }

    div#conter div.tn,
    div#allEntries div.tn {
        font-size: 18px;
        margin: 0;
        float: none;
        white-space: nowrap;
    }

    div#conter > div > li > div.tn,
    div#allEntries > div > li > div.tn {
        font-size: 18px;
        margin: 0 0 0 60px;
        float: none;
        white-space: nowrap;
        min-height: 35px;
    }

    div#conter div.rd,
    div#allEntries div.rd {
        float: left;
        margin: 0;
    }

    div#conter span.tc,
    div#allEntries span.tc {
        margin: 0;
    }

    div#conter > div > li:after,
    div#allEntries > div > li:after {
        content: 'i';
        font-family: Courier New, Courier, Lucida Sans Typewriter, Lucida Typewriter, monospace;
        display: block;
        font-size: 18px;
        height: 24px;
        width: 24px;
        color: #fff;
        background: #444;
        border-radius: 50%;
        line-height: 24px;
        text-align: center;
        font-weight: bold;
        position: absolute;
        right: 20px;
        top: 50%;
        margin-top: -12px;
        pointer-events: none;
        z-index: 13;
    }


    div#conter > div > li > span.tc,
    div#allEntries > div > li > span.tc {
        top: 8px;
        left: 50px;
    }

    div#conter > div > li.pplay > span.tc,
    div#allEntries > div > li.pplay > span.tc {
        left: 98px;
    }

    div#conter span.tc img,
    div#allEntries span.tc img {
        width: 50px;
        height: 50px;
    }

    div#conter div.ts,
    div#allEntries div.ts {
        float: left;
        font-weight: 400;
        color: #b3551c;
        margin-left: 10px;
        font-size: 12px;
    }

    div#conter div.ts + div.tai,
    div#allEntries div.ts + div.tai {
        opacity: 0;
        position: absolute;
        background: #fff;
        padding: 10px 15px 25px 15px;
        z-index: 12;
        font-size: 12px;
        top: 0px;
        width: 40px;
        right: 0px;
        box-shadow: 0 0 40px #00000066;
        transition: opacity .1s ease-out, width 0.1s ease-out;
        line-height: 1.2;
        white-space: nowrap;
        overflow: hidden;
    }

    div#conter div.ts + div.tai + div.tai,
    div#allEntries div.ts + div.tai + div.tai {
        opacity: 0;
        position: absolute;
        z-index: 12;
        font-size: 11px;
        top: 43px;
        width: 40px;
        right: 0px;
        padding: 0 15px;
        transition: opacity .1s ease-out, width 0.1s ease-out;
        pointer-events: none !important;
        color: #888;
        white-space: nowrap;
        overflow: hidden;
    }

    div#conter div.ts + div.tai:hover,
    div#conter div.ts + div.tai:hover + div.tai,
    div#allEntries div.ts + div.tai:hover,
    div#allEntries div.ts + div.tai:hover + div.tai {
        width: 430px;
        opacity: 1;
    }


    /*Filter block */
    div.filterBlock {
        padding: 0;
    }

    div.filterBlock ul#styles {
        width: auto;
        display: flex !important;
    }

    div.filterBlock ul#styles .st {
        flex-grow: 1;
    }

    div.filterBlock ul#styles li {
        font-size: 11px;
        color: #888;
        padding: 1px 5px;
        width: 100%;
        line-height: 1.3;
    }

    div.filterBlock ul#styles li:hover {
        background: #fff;
        color: #444;
    }

    div.filterBlock2 {
        padding: 10px 0;
    }

    div#footer {
        padding-bottom: 90px;
        color: #444;
        background: none;
    }
    /* Pagination */
    div.catPages1,
    div.catPages2 {
        padding: 10px 0;
    }

    span.pagesBlockuz1,
    span.pagesBlock1 {
        display: inline-block;
    }

    div.catPages2 b.swchItemA1,
    div.catPages2 a.swchItem1,
    div.catPages1 b.swchItemA,
    div.catPages1 a.swchItem,
    span.pagesBlockuz1 b.swchItemA,
    span.pagesBlockuz1 a.swchItem,
    span.pagesBlock1 b.swchItemA,
    span.pagesBlock1 a.swchItem,
    b.swchItemA,
    a.swchItem,
    b.swchItemA1,
    a.swchItem1 {
        background: #dedede;
        color: #555;
        display: inline-block;
        font-size: 11px;
        margin: 0 2px;
        text-align: center;
        text-decoration: none;
        border-radius: 2px;
        line-height: 32px;
        height: 32px;
        padding: 0 10px;
        width: auto;
        min-width: 20px;
        font-weight: 400;
    }

    div.catPages2 a.swchItem1:hover,
    div.catPages1 a.swchItem:hover,
    span.pagesBlockuz1 a.swchItem:hover,
    span.pagesBlock1 a.swchItem:hover,
    a.swchItem:hover,
    a.swchItem1:hover {
        background: #b1d497;
    }

    div.catPages2 b.swchItemA1,
    div.catPages1 b.swchItemA,
    span.pagesBlockuz1 b.swchItemA,
    span.pagesBlock1 b.swchItemA,
    b.swchItemA,
    b.swchItemA:hover,
    b.swchItemA1,
    b.swchItemA1:hover {
        font-weight: 400;
        color: #555;
        background: #b1d497;
    }

    div.catPages2 span.swchItemDots,
    div.catPages1 span.swchItemDots,
    span.pagesBlockuz1 span.swchItemDots,
    span.pagesBlock1 span.swchItemDots,
    span.swchItemDots {
        display: inline-block;
        width: 20px;
        text-align: center;
    }
    /* Switcher */
    .onoffswitch-label {
        display: block;
        overflow: hidden;
        cursor: pointer;
        border: none;
        border-radius: 3px;
    }

    .onoffswitch-inner {
        display: block;
        width: 200%;
        margin-left: -100%;
        transition: margin 0.3s ease-in-out 0s;
    }

    .onoffswitch-inner:before,
    .onoffswitch-inner:after {
        display: block;
        float: left;
        width: 50%;
        height: 20px;
        padding: 0;
        line-height: 20px;
        font-size: 10px;
        color: white;
        font-family: Arial, sans-serif;
        font-weight: 300;
        box-sizing: border-box;
    }

    .onoffswitch-inner:before {
        content: "ВКЛ";
        padding-left: 8px;
        background-color: #98b776;
        color: #FFFFFF;
    }

    .onoffswitch-inner:after {
        content: "ВЫКЛ";
        padding-right: 5px;
        background-color: #aaa;
        color: #fff;
        text-align: right;
    }

    .onoffswitch-switch {
        display: block;
        width: 16px;
        height: 16px;
        margin: 2px;
        background: #FFFFFF;
        position: absolute;
        top: 0;
        bottom: 0;
        right: 35px;
        border: none;
        border-radius: 3px;
        transition: all 0.3s ease-in-out 0s;
    }

    .onoffswitch-checkbox:checked + .onoffswitch-label .onoffswitch-inner {
        margin-left: 0;
    }

    .onoffswitch-checkbox:checked + .onoffswitch-label .onoffswitch-switch {
        right: 0px;
    }
    /* bottom player */
    div#p_setup + div#pageplayer div.plcntrl {
        background: linear-gradient(to top, #6d6f75, #8b8f97);
        border-top: 1px solid #71798a;
    }

    div#p_setup + div#pageplayer div.plcntrl a:hover {
        color: #fff;
    }

    div#p_setup + div#pageplayer rect.pt-center {
        fill: #444;
    }

    div#p_setup + div#pageplayer rect.pt-loading {
        fill: #bbb;
    }

    div#p_setup + div#pageplayer rect.pt-position {
        fill: #fff;
    }

    div#p_setup + div#pageplayer div#volume {
        background: #aaa;
    }

    div#p_setup + div#pageplayer div.ui-slider-range {
        background: #fff;
    }

    div#p_setup + div#pageplayer span.pt-current-track-title,
    div#p_setup + div#pageplayer div.pt-time {
        text-shadow: none;
    }

    div#p_setup + div#pageplayer:hover span.pt-current-track-title,
    div#p_setup + div#pageplayer:hover div.pt-time {
        opacity: 0.3;
    }
    /* player setup */
    div#p_setup {
        border-radius: 0;
        padding: 20px;
        box-shadow: 10px 10px 0 #00000022;
    }
    /* main page news */
    div#main_page_news div[id^=entry] {
        width: 340px;
        margin: 10px 15px 10px 0;
        background: none;
        overflow: visible;
    }

    div#main_page_news div[id^=entry]:nth-child(2n) {
        clear: left;
    }

    div#main_page_news div.news_post {
        width: 340px;
        padding: 30px 0 20px 0;
        height: auto;
        position: relative;
        border-bottom: 1px dotted #ccc;
    }

    div#main_page_news div.news_post:before {
        display: block;
        float: left;
        background: linear-gradient(135deg, #eee, #ddd);
        border: 8px solid #fafafa;
        box-shadow: 1px 1px 5px #00000011;
        width: 100px;
        height: 75px;
        content: 'No image';
        text-align: center;
        line-height: 75px;
        color: #aaa;
        text-shadow: 1px 1px 0 #fff;
        margin: 0 10px 5px 0;
    }

    div#main_page_news div.news_post br {
        display: none;
    }

    div#main_page_news div.news_post p {
        text-indent: 0;
        text-align: left !important;
        margin: 0 0 5px 0;
    }

    div#main_page_news div[id^=entry] div.news_post h3,
    div#main_page_news div[id^=entry] div.news_post h4,
    div#main_page_news div[id^=entry] div.news_post h5,
    div#main_page_news div[id^=entry] div.news_post span,
    div#main_page_news div[id^=entry] div.news_post b,
    div#main_page_news div[id^=entry] div.news_post strong,
    div#main_page_news div[id^=entry] div.news_post p,
    div#main_page_news div[id^=entry] div.news_post div {
        text-align: left !important;
        font-weight: 400 !important;
        font-size: 14px !important;
        font-family: Arial, sans-serif !important;
        text-decoration: none !important;
        text-transform: none !important;
        line-height: 1.3 !important;
    }

    div#main_page_news div.news_post a.nn {
        font-family: Arial, sans-serif;
        font-size: 18px;
        margin-bottom: 10px;
        position: absolute;
        top: 0;
        left: 0;
        width: 340px;
        overflow: hidden;
        white-space: nowrap;
        text-overflow: ellipsis;
        z-index: 1;
    }

    div#main_page_news div.news_post img {
        width: 100px !important;
        height: 75px !important;
        min-width: 0;
        min-height: 0;
        position: absolute;
        top: 30px;
        left: 0px;
        background: #fff;
        z-index: 2;
        transition: all 0.3s ease-in-out;
        border: 8px solid #fff !important;
    }

    div#main_page_news div.news_post img:hover {
        width: 200px !important;
        height: 150px !important;
        top: -2px;
        left: -42px;
        background: #fff;
    }

    div#main_page_news div.news_post iframe[src*="youtube"] {
        width: 340px !important;
        height: auto !important;
        position: absolute;
        top: 30px;
        left: 0;
    }
    /* Recommended */
    div#recot img {
        border-radius: 0;
    }
    /* comments */
    div.selector {
        clear: both;
        padding-bottom: 10px;
        margin: 10px 0;
    }

    div.com.cBlock1,
    div.com.cBlock2 {
        background: #fff;
        border-radius: 0;
        border: 0;
        box-shadow: 2px 2px 2px #00000011;
    }

    span.com_rating {
        margin-right: 5px;
    }

    div.com.cBlock1 > div[style^=float] a,
    div.com.cBlock2 > div[style^=float] a {
        font-size: 11px;
        color: #aaa;
    }

    div.com.cBlock1 > div[style^=float] a:hover,
    div.com.cBlock2 > div[style^=float] a:hover {
        color: #42a9d2;
    }

    span.report-spam-wrap {
        opacity: 1;
    }

    span.report-spam-wrap a {
        display: inline-block;
        margin: 0 10px 0 5px;
    }

    /* user playlist */
    div#user_playlist div[id^=entry] {
        width: auto;
    }

    div#user_playlist div.postinprofile > li {
        padding-left: 70px;
        background: #fff;
        min-height: 50px;
    }

    div#user_playlist div.postinprofile a.pt-link {
        right: 0;
    }

    div#user_playlist div.postinprofile span.tc {
        left: 10px;
        top: 10px;
    }

    div#user_playlist div.postinprofile > li.pplay {
        padding-left: 116px !important;
        margin-left: -46px !important;
        width: 703px !important;
    }

    div#user_playlist div.postinprofile > li.pplay span.tc {
        left: 56px;
    }

    /* music top */
    .t100 {
        border: none;
    }

    #top100m .pt-link {
        width: 55px;
        height: 55px;
        margin: 10px;
    }

    .t100:before {
        font-size: 54px;
        width: 75px;
        height: 75px;
        line-height: 75px;
        font-weight: bold;
    }

    .t100:nth-child(n + 10):before {
        font-size: 42px;
    }

    .t100:nth-child(n + 100):before {
        font-size: 32px;
    }

    .t100 .topEntryName {
        padding-left: 75px;
    }

    .topEntryName a {
        font-size: 18px;
        line-height: 65px;
        color: #000;
    }

    .topEntryName a:visited {
        color: #91a4b9;
    }

    .t100 .t1s {
        font-weight: 400;
        margin: 0;
        color: #444;
        float: none;
        z-index: 10;
        font-size: 10px;
        position: absolute;
        bottom: 13px;
        text-transform: uppercase;
        left: 95px;
    }

    .t100.pplay {
        background: #fff !important;
        position: relative !important;
        padding: 0 !important;
        margin: 0 !important;
        -webkit-box-shadow: 0px 0px 19px 0px rgba(235, 235, 235, 1);
        -moz-box-shadow: 0px 0px 19px 0px rgba(235, 235, 235, 1);
        box-shadow: 0px 0px 19px 0px rgba(0, 0, 0, 0.38);
        z-index: 1;
    }

    #top100m .t100 {
        height: 75px;
    }

    #top100m .t100 .tc img {
