@-moz-document url("http://clubtone.net/vip"),
    url("http://clubtone.do.am/vip") {
    /* VIP membership registration page */
    input[type=text] {
        box-sizing: border-box;
        font-size: 14px;
        border: 1px solid #ccc;
        padding: 2px 5px;
        height: 34px;
        line-height: 28px;
        color: #666;
        border-radius: 2px;
        vertical-align: top;
        background-color: #fff;
        margin: 0 0 5px 0;
    }

    h2 {
        font-size: 36px;
        text-align: left;
        color: #444;
        font-weight: 300;
    }

    td {
        color: #444;
        vertical-align: top;
        text-align: left;
        padding: 0;
    }

    td[colspan='2'] {
        padding-left: 35%;
    }

    textarea {
        box-sizing: border-box;
        font-family: Arial;
        font-size: 14px;
        border: 1px solid #ccc;
        padding: 2px 6px;
        color: #666;
        border-radius: 2px;
        vertical-align: middle;
        background-color: #fff;
    }

    input[type=submit] {
        box-sizing: border-box;
        font-size: 14px;
        text-shadow: 1px 1px 0 #fff;
        height: 34px;
        line-height: 32px;
        display: inline-block;
        white-space: nowrap;
        padding: 0 20px;
        border-radius: 2px;
        vertical-align: top;
        overflow: hidden;

        border: 1px solid #ccc;
        color: #444;
        background: #f5f5f5;
        background: linear-gradient(to top, #ccc 2%, #ddd 3%, #f5f5f5 97%, #fff 98%);
        border-bottom: 1px solid #aaa;

        cursor: pointer;
        text-decoration: none;
        user-select: none;
        -webkit-touch-callout: none;
        -webkit-user-select: none;
        -khtml-user-select: none;
        -moz-user-select: none;
        -ms-user-select: none;
        text-align: center;
        margin: 0 0 5px 0;
    }

    input[type=submit]:hover {
        border: 1px solid #bbb;
        color: #666;
        background: linear-gradient(to top, #ddd 2%, #f4f4f4 3%, #fafafa 97%, #fff 98%);
        border-bottom: 1px solid #aaa;
        text-decoration: none;
    }

    input[type=submit]:active {
        color: #222;
        line-height: 34px;
    }

    div#result + div {
        color: #999 !important;
        padding-top: 15px;
    }
}


