style.css:
@-moz-document url-prefix("https://catanuniverse.com/en/game") {
.template-wrap, .game-view{
    width: 100%;
}

.navbar{
    box-shadow: 0px 0px 1px 0px rgba(0,0,0,1) inset,0px 1px 0px 0px rgba(255,255,255,.125) inset, 0 0 8px 4px rgba(0,0,0,.25);
    background: #c70003;
}

body .custom-navbar .nav li a{
    font-weight: bold;
    font-size: 18px;
}

.template-wrap{
    margin-top: 50px;
}

.game-content-control{
    position: relative;
	z-index: 100;
    width: 38px;
}

body{
    background-color: #111;
}

footer{
    display: none;
}
}
