style.css:
@-moz-document url-prefix('https://dlm.carsforsale.com/'),
url-prefix('http://dlm.carsforsale.com/') {
/*
Lead Manager Theme - By David Becker
Install the Stylish browser extension
Chrome		https://chrome.google.com/webstore/detail/stylish/fjnbnpbmkenffdnngjfgmeleoegfcffe?hl=en
Firefox		https://addons.mozilla.org/en-US/firefox/addon/stylish/
Setup Instructions
1. Click the Stylish icon.
2. Click manage installed styles.
3. Click write new style.
4. Paste this entire code into the code window.
5. Below the code window click the specify button.
6. Fill in URLs starting with http://dlm.carsforsale.com/ and then URLs starting with https://dlm.carsforsale.com/
7. Click Save and you should see your new stylized DLM. Feel free the customize the colors in the CSS below.
*/
/* Import Fonts */
/* @import url("https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/css/font-awesome.min.css"); */
/* BG Image */
body {
	background-image: url("https://i.imgur.com/6jEifrz.png");
	background-repeat: both;
	background-repeat: repeat;
	background-attachment: fixed;
}

/* Round All Inputs */
input, select, textarea {
    border-radius:3px;
}

/* Font Awesome Stuff */
@font-face {
	font-family: 'FontAwesome';
	src: url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?v=4.7.0');
	src: url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.eot?#iefix&v=4.7.0') format('embedded-opentype'), url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff2?v=4.7.0') format('woff2'), url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.woff?v=4.7.0') format('woff'), url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.ttf?v=4.7.0') format('truetype'), url('https://stackpath.bootstrapcdn.com/font-awesome/4.7.0/fonts/fontawesome-webfont.svg?v=4.7.0#fontawesomeregular') format('svg');
	font-weight: normal;
	font-style: normal;
}

/* Colors */
#ctl00_phMain_lbMyTasks,
#ctl00_phMain_lbTasks,
#lnkSHHelp,
#ctl00_phMain_MyTasks1_lbTasks2,
.row1 A:link,
.row2 A:link {
	color: #2A5DCB !important;
}

.row1 A:link,
.row2 A:link {
	padding-left: 6px;
}

/* Rows */
.row1,
.row2,
.row1Large,
row2Large {
	background-color: #F0F0F0 !important;
}

.row1:hover,
.row2:hover,
.row1Large:hover,
row2Large:hover,
#parentDealerDocs .tableRow:hover {
	background-color: #e7e7e7 !important;
}

.row1 > td > div,
.row2 > td > div,
.row1 > td > div > div,
.row2 > td > div > div {
	-webkit-transition: 0.2s;
	-o-transition: 0.2s;
	transition: 0.2s;
	box-sizing: border-box;
	-moz-box-sizing: border-box;
	-webkit-box-sizing: border-box;
}

.row1:hover > td > div,
.row2:hover > td > div {
	background-color: #ececec !important;
	display: block !important;
	border: 1px solid #d9d9d9;
}

.row1:hover > td > div > div,
.row2:hover > td > div > div {
	background-color: #333 !important;
	display: block !important;
}

.row1 A:visited,
.row2 A:visited {
	color: #A0A0A0 !important;
}

.row1,
.row2 {
	border-right: 1px #E2E2E2 solid;
	border-left: 1px #E2E2E2 solid;
	border-Top: 1px #E2E2E2 solid;
	font-family: Arial;
	font-size: 12px;
	height: 26px;
}

/* Hide DS Dealer Image */
#ctl00_phMain_gvTasksGraphics .row1 img,
#ctl00_phMain_gvTasksGraphics .row2 img {
	display: none;
}

/* Rest */
#ctl00_phMain_lblWebsiteURl,
#ctl00_phMain_lblDemo,
#ctl00_phMain_lblDemo,
#ctl00_phMain_hlSiteBuilder,
.xlargeBold .normalBoldBlue,
.normalBoldBlue,
A:link,
#ctl00_phMain_hlGoToWebsite {
	color: #2A5DB0 !important;
}

#ctl00_phMain_gvTasks A:visited {
	color: #333;
}

#ctl00_phMain_DealerSiteSetup21_hlDealer {
	color: #BD001D !important;
}

#ctl00_phMain_DealerSiteSetup1_TabStrip1 A,
#rtsLevel rtsLevel1 A {
	color: #000000 !important;
}

.blueHeader A:link {
	color: #000067;
}

.blueHeader,
.blueHeader A:visited,
.blueHeader A:link {
	color: #FFFFFF !important;
}

/* Headers */
.xxlargeBold,
#ctl00_phMain_btnSaveReturnToDealer,
.blueHeader,
#ctl00_phMain_btnUpdateDealer,
.blueHeaderLarge,
.tableBorder .blueHeader {
	background-color: #333 !important;
}

/*Row Background Colors*/
#ctl00_phMain_trCFSProfile {
	background-color: #ffffff !important;
}

::-moz-selection,
::-moz-selection {
	background-color: #DCDCDC;
}

::selection,
::-moz-selection {
	background-color: #DCDCDC;
}

/*The Rest*/
#ctl00_phMain_gvTasks tr td span {
	background-color: transparent !important;
	/* This removes the ugly black background on new websites*/
}

.blueHeader td,
.blueHeader th {
	padding-right: 5px;
	padding-left: 5px;
}

.xlargeBold,
.blueHeader {
	font-family: Arial;
	font-size: 12px;
	font-weight: bold;
}

.blueHeader {
	height: 24px;
}

.xlargeBold .normalBoldBlue,
.blueHeader A:visited,
.blueHeader A:link,
A:link {
	text-decoration: underline;
	cursor: pointer;
}

#ctl00_phMain_btnUpdateDealer {
	width: 96%;
	height: 60px;
	margin-bottom: 10px;
	margin-top: 10px;
}

/*Fix some width issues */
#ctl00_phMain_upPage table {
	width: 100%;
}

#ctl00_phMain_rblCompleted,
#ctl00_phMain_rblReturnToGraphics {
	width: 100px !important;
}

/* Hide Top Demo Password & Time */
#ctl00_tableTitleMostTypes {
	display: none;
}

script + div > table[style="width: 100%"] > tbody > tr:nth-child(1) {
	display: none;
}

/* Set on duty names horizontal */
#ctl00_phMain_divGraphics > .tableBorder:nth-child(1) tr,
.tableBorder:nth-child(2) tr {
	display: -webkit-inline-box;
	display: -ms-inline-flexbox;
	display: inline-flex;
	padding: 2px 10px;
}

#ctl00_phMain_divGraphics > .tableBorder:nth-child(1),
#ctl00_phMain_divGraphics > .tableBorder:nth-child(2) {
	border: 0 solid black;
	/*margin:-20px auto 5px auto;*/
}

/* Removed Demo Password bar and time and date bar */
#ctl00_phMain_upPage {
	margin-top: -13px;
}

/* ---------------------------------------------------- DLM START -------------------------------------------- */
/* DLM Login Page */
#txtUsername,
#txtPassword {
	height: 40px;
	width: 376px;
	padding: 0 10px;
	margin: 5px;
}

#btnSubmit[value="Login"] {
	display: block;
	padding: 10px;
	width: 97%;
	background-color: #111;
	border: none;
	color: #fff;
}

#hlResetPassword {
	margin: 10px 0;
	display: inline-block;
	color: #b9b9b9 !important;
}

/* Change Left Navigation Menu */
#ctl00_navMenu {
	margin: 1px 0 0 -8px;
	border-radius: 0 5px 5px 0;
	border: 1px solid rgba(0, 0, 0, 0.08);
	border-left: none;
	-webkit-transition: 0.2s;
	-o-transition: 0.2s;
	transition: 0.2s;
	overflow: hidden;
}

#ctl00_navMenu:hover {
	left: 6px;
}

.siteMenu {
	background-color: #e7e7e7;
	border: none;
	border-left: 6px solid #111;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	border-bottom: 2px solid rgba(0, 0, 0, 0.08);
}

.siteMenu:hover {
	background-color: #001c4d;
	border: none;
	border-left: 4px solid #001c4d;
	border-bottom: 2px solid #001c4d;
	cursor: pointer;
}

.siteMenu a {
	display: block;
	padding: 10px 6px;
	color: #111 !important;
	text-decoration: none;
	margin-left: 10px;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	font-weight: 200;
	-webkit-user-drag: none;
}

.siteMenu:hover a {
	color: #fff !important;
}

#ctl00_navMenu tr:nth-child(1) .siteMenu a {
	padding: 24px 0px 24px 4px;
}

/* Round all Table corners */
#ctl00_phMain_gvTasksGraphics.tableBorder,
#ctl00_phMain_gvTaskReview.tableBorder {
	border-radius: 5px;
	overflow: hidden;
}

/* Set Max Height on QC List */
#ctl00_phMain_gvTaskReview {
	overflow-y: scroll !important;
	border: none;
	width: 100% !important;
	height: 700px;
	display: inline-block;
}

#ctl00_phMain_gvTaskReview tbody tr {
	width: 100%;
}

/* Tasks Page Make Dates on other open tasks easier to click */
#ctl00_phMain_gvTasks tr td:nth-child(1) a {
	background-color: #ccc;
	padding: 10px 30px;
	color: #666 !important;
	font-size: 12px;
	display: block;
	text-decoration: none;
	border-radius: 5px;
	margin: 5px 5px;
	-webkit-transform: 0.2s;
	-ms-transform: 0.2s;
	transform: 0.2s;
}

#ctl00_phMain_gvTasks tr td:nth-child(1) a:hover {
	color: #111 !important;
}

/* Pad Title */
#ctl00_phMain_gvTasksGraphics tr th,
#ctl00_phMain_gvTaskReview tr th {
	padding: 10px;
}

/* Clean Header on tasks page */
#ctl00_phMain_lbTasksGraphics {
	margin-right: 5px;
}

#ctl00_phMain_ddlDealerInventoryTypeGraphics {
	margin-left: 3px;
}

#ctl00_phMain_lbTasksGraphics + br {
	display: none;
}

#ctl00_phMain_ddlTasksTypeGraphics,
#ctl00_phMain_ddlDealerInventoryTypeGraphics {
	padding: 5px;
	margin-bottom: 3px;
	background-color: #F6F6F6;
	border: 1px solid #ddd;
	border-radius: 5px;
	font-size: 12px;
}

/* Remove Useless Columns */
/* #ctl00_phMain_gvTasksGraphics tr td:nth-child(1),
#ctl00_phMain_gvTasksGraphics tr th:nth-child(1), */
#ctl00_phMain_gvTasksGraphics tr td:nth-child(5),
#ctl00_phMain_gvTasksGraphics tr th:nth-child(5),
#ctl00_phMain_gvTasksGraphics tr td:nth-child(8),
#ctl00_phMain_gvTasksGraphics tr th:nth-child(8) {
	display: none;
}

/* Remove TaskID Column */
#ctl00_phMain_gvTasksGraphics tr td:nth-child(7),
#ctl00_phMain_gvTasksGraphics tr th:nth-child(7) {
	display: none;
}

/* Change Numbered Column */
#ctl00_phMain_gvTasksGraphics tr th:nth-child(1) {
	font-size: 0px;
	display: block;
	padding: 0;
	position: relative;
	width: 10px;
	height: 30px;
	overflow: hidden;
}

#ctl00_phMain_gvTasksGraphics tr th:nth-child(1):before {
	content: "#";
	position: absolute;
	top: 6px;
	left: 4px;
	display: block;
	width: 10px;
	font-size: 12px;
}

#ctl00_phMain_gvTasksGraphics tr td:nth-child(1) {
	font-size: 12px;
	width: 20px;
	overflow: hidden;
	display: block;
	margin-top: 10px;
	margin-left: 4px;
	color: #a5a5a5;
}

/* Space out Tasks Page */
#ctl00_phMain_ucTasks_gvTasks td {
	padding: 10px;
}

/* DLM - Custom Site Info Tab Domains Tab Fix */
#tblCustomDomains .row2Large td {
	width: 100%;
	position: relative;
}

#tblCustomDomains .row2Large td:after {
	content: "";
	width: 15px;
	right: -15px;
	height: 100%;
	background-color: #ddd;
	position: absolute;
	top: 0;
}

/* Dealer Details Tab - Site Builder Buttons */
#ctl00_phMain_hplSetupSmartSite,
#ctl00_phMain_hplDynamicSite {
	display: inline-block;
	padding: 10px 20px;
	background-color: #ddd !important;
	color: #111 !important;
	border: 1px solid transparent;
	text-decoration: none;
	margin: 6px 0;
	float: right;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	line-height: 16px;
	-webkit-user-drag: none;
}

#ctl00_phMain_hplSetupSmartSite:before,
#ctl00_phMain_hplDynamicSite:before {
	font-family: FontAwesome;
	margin-right: 8px;
	font-size: 22px;
	content: "\f044";
	top: 4px;
	position: relative;
}

#ctl00_phMain_hplSetupSmartSite:hover,
#ctl00_phMain_hplDynamicSite:hover {
	background-color: transparent !important;
	border: 1px solid rgba(256, 256, 256, 0.2);
	color: #eee !important;
}

/* Dealer Details Page - Type of Site Highlight TABs */
#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"],
#ctl00_phMain_rtsContact a.rtsLink[style="color:Red;background-color:Red;border-color:Red;border-style:Double;font-weight:bold;"] {
	border: none !important;
	background-color: transparent !important;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Red;background-color:Red;border-color:Red;border-style:Double;font-weight:bold;"] .rtsOut {
	/* Billing Info Tab */
	background-image: none;
	background-color: #900000;
	border-top: 5px solid #620000;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	font-weight: 700;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"] .rtsOut {
	background-image: none;
	background-color: #900000;
	border-top: 5px solid #620000;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	font-weight: 700;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"].rtsSelected .rtsOut {
	background-color: #bc1b1b;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"].rtsSelected .rtsOut:hover {
	background-color: #bc1b1b;
}

#ctl00_phMain_rtsContact a.rtsLink[style="color:Blue;background-color:Blue;border-color:Blue;border-style:Double;font-weight:bold;"] .rtsOut:hover {
	background-image: none;
	background-color: #bc1b1b;
}

/* DLM Tasks Page - Style Back to Dealer Button */
#ctl00_phMain_hlBack {
	display: inline-block;
	padding: 10px 20px;
	background-color: #ddd;
	color: #111 !important;
	text-decoration: none;
	margin: 6px;
	float: right;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
	border: 1px solid transparent;
}

#ctl00_phMain_hlBack:hover {
	background-color: transparent;
	border: 1px solid rgba(256, 256, 256, 0.2);
	color: #eee !important;
}

#ctl00_phMain_hlBackTask,
#ctl00_phMain_affiliateDetials_hlBackTask {
	padding: 4px 10px;
	margin: 12px;
	display: inline-block;
	color: rgba(256, 256, 256, 0.6) !important;
	border: 1px solid rgba(256, 256, 256, 0.3);
	text-decoration: none;
	-webkit-transition: .2s;
	-o-transition: .2s;
	transition: .2s;
}

#ctl00_phMain_hlBackTask:hover,
#ctl00_phMain_affiliateDetials_hlBackTask:hover {
	background-color: #ddd;
	border: 1px solid transparent;
	color: #111 !important;
}

#ctl00_phMain_uprogMain + table .blueHeader .right br {
	display: none;
}

#ctl00_phMain_uprogMain + table .blueHeader td:first-child {
	border-top-left-radius: 5px;
	border-bottom-left-radius: 5px;
	padding-left: 10px;
}

#ctl00_phMain_uprogMain + table .blueHeader td.right {
	border-top-right-radius: 5px;
	border-bottom-right-radius: 5px;
}

/*
#ctl00_phMain_upPage > table > tbody > tr{
display: block;
}
#ctl00_phMain_upPage > table > tbody > tr:nth-child(1){
display: table-row;
}
#ctl00_phMain_upPage > table > tbody > tr td:nth-child(1){
width:150px;
}
*/
/* CC Profile - Set Login button bigger for CC Profile Tab */
a .usrLogin {
	width: 90px;
	height: 40px;
	background-color: #122f60 !important;
	color: #fff;
	border: none;
	border-bottom: 4px solid #000233;
	padding: 10px;
	-webkit-transition: .1s;
	-o-transition: .1s;
	transition: .1s;
	border-radius: 4px;
	height: 40px;
	cursor: pointer;
}

a .usrLogin:hover {
	background-color: #000233 !important;
	color: #fff;
	border-bottom: 0px solid #000233;
	padding: 10px;
	cursor: pointer;
	top: 2px;
}

a.usrLogin {
	text-decoration: none !important;
	-webkit-user-drag: none;
}

/* CC Profile - Lot ID's */
#ctl00_phMain_ctl05_upDealerInfo + div > div {
	background-color: #fff;
	padding: 5px;
	margin-bottom: 6px;
	border-radius: 10px;
}

#ctl00_phMain_ctl05_upDealerInfo + div > div > span:nth-child(1) {
	text-align: center;
	padding: 10px;
	background-color: #bc1b1b;
	color: #fff;
	width: 70px !important;
	display: inline-block;
	border-radius: 10px 0 0 10px;
	font-weight: 900;
}

#ctl00_phMain_ctl05_upDealerInfo + div > div > span:nth-child(2) {
	text-align: center;
	padding: 10px;
	background-color: #122f60;
	color: #fff;
	border-radius: 0 10px 10px 0;
	margin-left: -3px;
	font-weight: 900;
}

#ctl00_phMain_ctl05_upDealerInfo + div > div:nth-child(1) > span {
	background-color: transparent;
	color: #000;
	text-align: left;
	font-weight: 600;
}

#ctl00_phMain_ctl05_upDealerInfo + div > div:nth-child(1) {
	background-color: transparent;
}

/* Task Details - Text Adjustments */
#ctl00_phMain_lblStatus,
#ctl00_phMain_lblWebsiteURl,
#ctl00_phMain_lblDemo,
#ctl00_phMain_lblUserID {
	margin: 2px 0;
	display: inline-block;
}

#ctl00_phMain_lblCreatedBy,
#ctl00_phMain_lblPhone {
	display: block;
	padding: 10px 10px;
	background-color: rgba(255, 255, 255, 0.5);
	border: 1px solid #ddd;
	color: #111;
	text-align: center;
	width: 212px;
	font-weight: 600;
}

#ctl00_phMain_lblCreatedBy {
	background-color: #ddd;
	margin: 10px 0 -2px;
	border-radius: 5px 5px 0 0;
	padding: 5px 10px;
}

#ctl00_phMain_lblPhone {
	font-size: 17px;
	border-radius: 0 0 5px 5px;
}

#ctl00_phMain_lblDealerName {
	font-weight: 600;
	font-size: 19px;
}

#ctl00_phMain_lblDealerID {
	background-color: #122f60;
	color: #fff;
	font-weight: 600;
	font-size: 16px;
	height: 16px;
	padding: 6px 10px;
	display: inline-block;
	border-radius: 6px;
	margin: 2px 20px 2px 0;
}

#ctl00_phMain_lblOldSubTaskType {
	color: #bbb;
}

#ctl00_phMain_lblError:after {
	content: "Publish Site";
	display: block;
	width: 386px;
	color: #fff;
	background-color: #126021;
	text-align: center;
	padding: 10px;
	margin-top: 1em;
	border-radius: 4px;
}

#ctl00_phMain_lblDealerName {
	display: inline-block;
	max-width: 550px;
}

/* Task Details - Set Buttons */
#ctl00_phMain_btnTask,
#ctl00_phMain_btnStatus,
#ctl00_phMain_btnCancel,
#ctl00_phMain_btnNote {
	height: 60px;
	display: inline-block;
	background-color: #122f60 !important;
	color: #fff;
	border: none;
	border-bottom: 4px solid #000233;
	padding: 10px 19px;
	-webkit-transition: .1s;
	-o-transition: .1s;
	transition: .1s;
	border-radius: 4px;
	height: 40px;
	cursor: pointer;
	margin-right: 1px;
}

#ctl00_phMain_btnTask:hover,
#ctl00_phMain_btnStatus:hover,
#ctl00_phMain_btnCancel:hover,
#ctl00_phMain_btnNote:hover {
	background-color: #000233 !important;
	border-bottom: 0px solid #000233;
	cursor: pointer;
	top: 2px;
}

#ctl00_phMain_btnStatus,
#ctl00_phMain_btnNote {
	background-color: #ccc !important;
	color: #111;
	border-bottom: 4px solid #444;
}

#ctl00_phMain_btnStatus:hover,
#ctl00_phMain_btnNote:hover {
	background-color: #aaa !important;
}

#ctl00_phMain_btnCancel {
	background-color: #C10019 !important;
	color: #fff;
	border-bottom: 4px solid #730000;
}

#ctl00_phMain_btnCancel:hover {
	background-color: #D50E22 !important;
}

/* Account Center PTO Text */
#ctl00_phMain_lblStartDate,
#ctl00_phMain_lblAnnualPTOAllotmentDate,
#ctl00_phMain_lblPTOAmount,
#ctl00_phMain_lblPTOHoursPast,
#ctl00_phMain_lblPTOHoursFuture,
#ctl00_phMain_lblPTOTotalUsed,
#ctl00_phMain_lblPTOHoursRemaining {
	font-weight: 600 !important;
	background-color: #C10019;
	color: #fff !important;
	width: 100px;
	padding: 10px;
	text-align: center;
	display: inline-block;
	margin-left: 10px;
}

#ctl00_phMain_btnCancel + div > div:nth-child(1) {
	display: none;
}

#ctl00_phMain_btnCancel + div > div:nth-child(2) {
	margin-top: 1em;
}

#ctl00_phMain_btnCancel + div > div,
#ctl00_phMain_btnCancel + div > div:nth-child(7) > div {
	background-color: #eee;
	margin-bottom: 5px;
	display: inline-block;
