style.css:
/* GotQuestions dark theme - 23/03/2018 by Bowain */
@-moz-document domain("www.gotquestions.org") {
nav, .logo img{ background-color: #34495E !important; }
 .search.closed, .search>form input[name=zoom_query], .search>form input.zoom_searchbox, 
.search>form input.zoom_searchbox:hover, .search.open,.search:hover, 
.search:hover>form input[name=zoom_query], .search.open>form input[name=zoom_query],
.search.open>form input[name=zoom_sort], .search>form input.zoom_searchbox:active,
.search, .search:active, .search:hover { background-color: #34495E !important; color: #17202A !important; }
.social, .rtTooltipBody { background-color: #1C2833 !important; }
.rtTooltipBody p { color: #F0F3F4 !important; }
.grid--bg_gray, .subscribe { background-color: #17202a !important; }
#crossRefGM { background-color: #17202A; border: 2px solid #17202A; color: #F7F9F9 !important; }
#crossRefGM a:hover { color: #6497A9 !important; }
.hero-questions, .rtTooltipHeader, .rtTooltipFooter { background-color: #17202A !important; }
.hero-grid { color: #17202A !important; }
footer, .card  { background-color: #17202A !important; }
.links a, .logo a { color: #F7F9F9 !important; }
body, .network { background-color: #273746 !important; }
html, .question .contents { color: #F0F3F4 !important; }
}
