style.css:
@-moz-document domain("app.asana.com") {
.full-width-header-compact-toggle, .PageHeaderStructure-collapseIcon {
    display: none;
}

.SidebarHeader-asanaLogo, .SidebarPlaceholder-asanaLogo {
    background-image: url(https://dudesign.pe/wp-content/uploads/2016/12/VivoWhite200x44.png);
    background-size: 120px;
    height: 26px;
    margin: 15px 0;
}

.CustomScrollbarContainer {
    padding-top: 11px;
}

.FavoriteStar-notFavorited, .ProjectDropdownMenuButton-downIcon {
    fill: #eeeeee;
}

.Avatar--medium {
    min-width: 24px;
}

.PageHeaderStructure-title {
    font-weight: 700;
    padding-left: 10px;
    padding-right: 10px;
}

.projectHeaderFacepile-content div {
    opacity: 0.8;
    transition: opacity 250ms;
}

.projectHeaderFacepile-content div:hover {
    opacity: 1;
}

.Avatar--large, .Avatar--medium, .Avatar--small {
    height: 24px;
    line-height: 24px;
    width: 24px;
}

.TokenButton-icon {
    height: 24px;
    width: 24px;
}

.Tab {
    font-size: 11px;
    font-weight: 400;
    text-transform: uppercase;
}

.Tab.Tab--isSelected {
    box-shadow: inset 0 -3px rgba(0, 0, 0, 0);
    opacity: 1;
    font-weight: 800;
}

.Topbar {
    border: none;
}

.TopbarSearch input, .Topbar-accountInfo div {
    opacity: 0.2;
    transition: opacity 250ms;
}

.TopbarSearch div:hover, .Topbar-accountInfo div:hover {
    opacity: 1;
}

.remix-topbar {
    justify-content: space-between;
}

.Topbar-left {
    -webkit-box-flex: 0;
    -ms-flex-positive: 0;
    flex-grow: 0;
    -ms-flex-negative: 0;
    flex-shrink: 1;
    margin: 0;
    -ms-flex-preferred-size: 0;
    flex-basis: 0;
}

.TopbarSearch {
    -webkit-box-flex: 0;
    -ms-flex-positive: 0;
    flex-grow: 0;
    -ms-flex-negative: 0;
    flex-shrink: 0;
    padding-left: 20px;
    margin: 0;
}

.Topbar-accountInfo {
    -webkit-box-flex: 0;
    -ms-flex-positive: 0;
    flex-grow: 0;
    -ms-flex-negative: 0;
    flex-shrink: 0;
    -webkit-box-flex: 1;
    -ms-flex-positive: 1;
    flex-grow: 1;
    margin: 0;
}

.TopbarSearch-input {
    max-width: 100%;
    border: none;
    font-size: 10px;
    text-transform: uppercase;
}

.TopbarSearch-input:focus, .TopbarPlaceholder-searchBox:focus, .TopbarSearch-input:active, .TopbarPlaceholder-searchBox:active, .TopbarSearch input:focus, .TopbarSearch input:active {
    border: 1px rgba(82, 4, 4, 0.27) solid;
    box-shadow: none;
    -webkit-animation: none;
    animation: none;
    opacity: 1;
}

.omnibutton {
    order: -1;
}

.Topbar-navButtonContainer {
    margin: 0;
}

.Topbar-myTasksButton {
    margin-left: 5px;
}

.is-selected {
    font-weight: 700;
}

.tab-nav-div .tab-nav-bar .tab-nav-button:not(.disabled).is-selected {
    font-weight: 800;
}

.tab-nav-div, .Topbar-left {
    text-transform: uppercase;
    font-size: 10px;
    font-weight: 600;
    padding-top: 2px;
    padding-left: 19px;
}

.ql-editor:focus {
    max-height: 80vh !important;
}

.ProjectOverview-statusUpdateSection, .ProjectOverview-descriptionSection {
    background-color: rgba(0, 0, 0, 0);
    border: none;
    box-shadow: none;
    opacity: 0.7;
}

.ProjectOverviewDescription-seeMoreLink {
    background-color: rgba(0, 0, 0, 0);
}

.SidebarTeamsList {
    border-top: 1px rgba(0, 0, 0, 0.2) dotted;
}

.SidebarRecentsAndFavorites-showMoreOrLessButton, .SidebarTeamDetailsProjectsList-showMoreProjectsLink, .SidebarTeamArchivedProjectsList-showArchivedProjectsLink, .LockIcon {
    opacity: 0.6;
}

.DownTriangleIcon {
    opacity: 0;
}

.RightTriangleIcon {
    margin-top: 2px;
}

span.SidebarTeamSettingsMenu-button {
    opacity: 0.1;
    transition: opacity 250ms;
}

span.SidebarTeamSettingsMenu-button:hover {
    opacity: 0.5;
}

.SidebarTeamMembershipHeader-teamName {
    font-weight: 700;
}

.SidebarRecentsAndFavorites-showMoreOrLessButton div:hover {
    opacity: 1;
}

.SidebarItemRow.is-selected {
    background-color: RGBA(241, 87, 112, .3);
    font-weight: 700;
}

.SidebarTeamMembership-dropTargetRow {
    margin-top: 5px;
}

.SidebarTeamMembership-dropTargetRow:after {
    background-color: rgba(27, 36, 50, 0.5);
    content: "";
    height: 1px;
    left: 15px;
    position: absolute;
    right: 15px;
    top: 0;
    display: none;
}

.SidebarTeamDetailsProjectsList-emptyTeam {
    display: none;
}

.StoryFeed-miniStory div, .small-feed-story-group > div {
    opacity: 0.3 !important;
    transition: opacity 250ms;
}

.StoryFeed-miniStory div:hover, .small-feed-story-group > div:hover {
    opacity: 1 !important;
}

.MiniStory div {
    opacity: 0.6 !important;
}

.MiniStory div:hover {
    opacity: 1 !important;
}

.StoryFeed-activitySeparator {
    display: none;
}

.FeedMiniStory-story {
    display: flex;
    justify-content: space-between;
}

.taskFeedView-feed {
    border-top: none !important;
}

.CustomPropertyDetailsContainer {
    border-top: none;
    border-bottom: none;
    background-color: #f9f9f9;
    margin-left: -30px;
    margin-right: -30px;
}

.CustomPropertyTextValueInput .autogrowTextarea-input, .CustomPropertyTextValueInput .autogrowTextarea-shadow, .CustomPropertyNumberValueInput input {
    background: none;
}

.TaskCommentComposer-composer {
    border: 0px;
}

.TaskCommentComposer-composer {
    background: rgba(0, 0, 0, 0);
    box-shadow: inset 0 0 #000000 !important;
    border: none;
}

.SingleTaskPane-titleRow textarea, .details-pane-title .header-name {
    font-weight: 700 !important;
}

.blockStoryView-metadata {
    opacity: 0.5;
}

.itemListInboxThreadView-footer {
    opacity: 0.3;
}

.inboxToolbar, .inbox-followup-elements {
    display: none;
}

.inboxThreadView {
    border-bottom: none;
    border: 1px #eee solid;
    padding-left: 30px;
    margin-bottom: 5px;
    box-shadow: 0px 0px 0px rgba(0, 0, 0, 0.2);
    background-color: white;
}

.notifications #center_pane, .notifications #right_pane {
    box-shadow: none;
    background-color: rgba(0, 0, 0, 0);
    border: none;
}

div.loading-boundary {
    margin-right: 5px;
}

.inboxThreadView.is-selectable.has-selectedNotification {
    background: RGBA(241, 87, 112, .1);
}

.inboxThreadView-typeHeader, .itemListGroupView-zipper {
    opacity: 0.2;
    transition: opacity 250ms;
}

.inboxThreadView-typeHeader:hover {
    opacity: 1;
}

.inbox .inbox-thread-wrapper .story.selectable {
    box-shadow: 0px 0px 5px rgba(0, 0, 0, .1);
    border: none;
}

.SectionRow {
    margin-top: 30px;
}

.SectionRow-sectionName, .group_header .drop-indicator .expandable {
    font-weight: 800 !important;
    font-size: 18px;
    border-bottom: 1px solid rgba(0, 0, 0, 0.53);
}

.DueDateContainer-dueDate {
    text-transform: uppercase;
}

.topbarContingentUpgradeButton-button.TopbarPageHeaderGlobalActions-upgradeButton {
