style.css:
@-moz-document domain("userstyles.org") {
/* Main */
html, body {
    background-color: #2b333b !important;
    color: #b3b3b3 !important;
    text-align: justify !important;
}
h1, h2, h3, h4, h4 a, .style_title, #title_long a, .header-search .search_input.focused .input_text_search {
    color: #b3b3b3 !important;
}
a {
    color: #fff !important;
    border: 0 !important;
}
h4 a:hover, h4 a:visited, #title_long a:hover, #title_long a:visited {
    color: #999999 !important;
}
div, span, #nav-view, #headline, #information_div {
    /* background-color: #2e3e49 !important; */
    background-color: #2b333b !important;
    color: #b3b3b3 !important;
}
.style_card_long .adContainer {
    display: none !important;
}
#install_style_button {
    border: 1px solid #29293d !important;
}
.logo_navigation_link {
    filter: invert(100%);
}
}
